INSERT INTO `drinks` (`id`, `drink_title`, `drink_recipe`, `drink_history`, `drink_type_en`, `drink_type_rus`, `drink_img`, `is_alco`) VALUES
(9, 'Водка энергетик мини', 'Налей в шот пополам сначала водку потом энергетик\r\n\r\n', 'Маленький Бомбини очень любил выпить.\r\n\r\nБармены всегда смеялись над ним и наливали ему водку в мензурку. Душа &laquo;маленького&raquo; человека была большой, и однажды, добавив к водке энергетик, Бомбини взлетел на небосвод славы.', 'shot', 'Шоты', 'drink_img/shot/vodka_energy_drink_mini-big.png', '1'),
(10, 'Мандарин шот', 'Налей в стопку водку мандариновую 50 мл\r\nУкрась физалисом\r\nВыпей шот и заверши его физалисом\r\n\r\n', 'Свое название мандарин получил благодаря тому, что его ароматные оранжевые плоды были излюбленным лакомством древних китайских правителей &mdash; мандаринов.\r\nТак же как и мандариновые коктейли, подававшиеся на древних китайских вечеринках.', 'shot', 'Шоты', 'drink_img/shot/mandarin_shot-big.png', '1'),
(11, 'Пилот', 'Налей в шот пополам сначала абсент потом энергетик', 'Давно это было. Денег на учебные полеты выделялось мало.\r\n\r\nНо какой пилот может прожить без неба? Так и появился прекрасный коктейль, запустивший в небо не одного пилота!', 'shot', 'Шоты', 'drink_img/shot/pilot-big.png', '1'),
(12, 'Цитрон шот', 'Налей в стопку водку цитрон 50 мл\r\nУкрась долькой ананаса\r\nВыпей шот и заверши его ананасом\r\n', 'Задолго до начала нашей эры цитрон был изображен на древних римских и византийских монетах.\r\n\r\nЦитрусы были настолько почитаемы, что коктейли с ними обменивались на монеты по курсу 3 к 1.', 'shot', 'Шоты', 'drink_img/shot/citron_shot-big.png', '1'),
(13, 'Ангельские сиськи', 'Налей в стопку самбуку светлую 25 мл\r\nИспользуя коктейльную ложку, уложи слой яичного ликера 25 мл\r\nУкрась коктейльной вишней\r\n\r\n', 'Журнал Playboy издается с 1955 года. За это время на страницах глянцевого журнала было опубликовано более 19258 фотографий самых красивых девушек планеты топлесс.\r\n\r\nОригинальный респект великому Хью Хефнеру выразил московский бармен, придумавший в честь его тяжких трудов столь же манящий коктейль.', 'shot', 'Шоты', 'drink_img/shot/angel_s_tits-big.png', '1'),
(14, 'Зеленый луч', 'Налей в стопку самбуку 25 мл\r         Используя коктейльную ложку, уложи слой шартреза зеленого 25 мл\r \r ', 'Как рассказывают старые матросы, появившийся на горизонте зеленый луч говорит о том, что чья-то душа пришла в наш мир из другого.\r\n\r\nНовые души знают ответы на все вопросы и всегда рады немного поболтать. С недавних пор вызвать их для беседы можно куда угодно, заманив особым коктейлем.', 'shot', 'Шоты', 'drink_img/shot/green_beam-big.png', '1'),
(16, 'Золотая текила бум', 'Налей в стопку золотую текилу 30 мл и спрайт почти доверху\r         Закрой стопку ладонью и ударь дном о стол\r         Быстро пей!\r \r ', 'Говорят, маленький вариант Текила бум придумали байкеры из знаменитого клуба &laquo;Hells Angels&raquo;.\r\n\r\nПутешествуя по южным широтам, они перевозили с собой целые передвижные бары, в которых можно было найти всё, кроме льда и больших стаканов. В связи с чем рецептуру любимого коктейля пришлось слегка изменить.', 'shot', 'Шоты', 'drink_img/shot/gold_tequila_slammer-big.png', '1'),
(17, 'Курант шот', 'Налей в стопку водку черносмородиновую 50 мл\r         Укрась ягодой ежевики на шпажке\r         Выпей шот и заверши его ягодой ежевики\r \r ', 'Вопреки ожиданиям многих, &laquo;курант&raquo; обозначает не только смородину, но и французский пестик для растирания красок.\r\n\r\nГоворят, ни один живописный шедевр, вышедший из-под кисти Моне, Мане или Дега не обходился без французских пестиков и смородиновых коктейлей.', 'shot', 'Шоты', 'drink_img/shot/kurant_shot-big.png', '1'),
(18, 'Малиновый шот', 'Налей в стопку водку малиновую 50 мл\r         Укрась малинкой на шпажке\r         Выпей шот и заверши его малиной\r \r ', 'Жил-был принц. Любил он шот.\r\n\r\nТот, кто пробовал &mdash; поймет. И однажды на заре стало шумно во дворе. Глянь в окно &mdash; а там принцесса. &laquo;Как чудесно!&raquo; &mdash; решил он. И позвал ее в свой дом&hellip;', 'shot', 'Шоты', 'drink_img/shot/raspberry_shot-big.png', '1'),
(19, 'Тик так', 'Налей в стопку мятный ликер зеленый 20 мл\r         Используя коктейльную ложку, уложи слой самбуки светлой 40 мл\r \r ', 'Свежесть общения всего в 2 шотах.\r\n', 'shot', 'Шоты', 'drink_img/shot/tic_tac-big.png', '1'),
(20, 'Халк', 'Налей в стопку айриш крим 25 мл\r         Используя коктейльную ложку, уложи слой шартреза зеленого 25 мл\r \r ', 'Герой комиксов Халк появился в 1962 году, благодаря Стэну Ли и Джеку Кирби.\r\n\r\nПоклонники супергероя считают, что превращение человека в зеленого гиганта произошло не из-за взрыва гамма-бомбы, а из-за засекреченного коктейля. И многие верят: можно обрести сверхчеловеческую силу, если не бояться смелых экспериментов.', 'shot', 'Шоты', 'drink_img/shot/hulk-big.png', '1'),
(21, 'Храбрый бык', 'Налей в стопку кофейный ликер 25 мл\r         Используя коктейльную ложку, уложи слой серебряной текилы 25 мл\r \r ', 'Говорят, каждый уважающий себя мексиканец, хотя бы однажды должен посетить испанскую корриду.\r\n\r\nА если среди тортильяс под харабе времени на отпуск уже не остаётся, можно победить храброго быка прямо у себя дома, легко воссоздав его в коктейльной форме!', 'shot', 'Шоты', 'drink_img/shot/brave_bull-big.png', '1'),
(22, 'Эль Бандито', 'Налей в стопку шоколадную текилу 50 мл\r         Налей в бокал пиво темное 300 мл\r         Выпей шот и заверши его пивом\r \r ', '&quot;Эй, не приставай к моей милашке, а не то узнаешь, что к чему&hellip;&quot;\r\n\r\nЭту песню напевал главный герой рассказа О. Генри - безжалостный и тщеславный кабальеро Франсиско, после того как подстроил убийство своей возлюбленной. Сегодня расстаться с девушкой гораздо проще, стоит лишь сделать глоток темного пива и запить его шоколадной текилой.', 'shot', 'Шоты', 'drink_img/shot/el_bandito-big.png', '1'),
(23, 'Бадминтон', 'Налей в стопку темный ром 20 мл и долей брусничный морс доверху\r         Укрась треугольничком ананаса\r         Выпей шот и заверши его ананасом\r \r ', 'Согласно проведенным исследованиям, бадминтон &mdash; вторая по популярности игра в мире, после футбола.\r\n\r\nЧем объясняется этот феномен социологи не могут объяснить. Вероятно, в число исследуемых попали любители не только спорта, но и коктейлей!', 'shot', 'Шоты', 'drink_img/shot/badminton-big.png', '1'),
(24, 'Би-Блэк', 'Налей в шот темный ром 50 мл\r         Посыпь дольку апельсина молотой корицей\r         Выпей шот и заверши его апельсином для полноты ощущений\r \r ', 'Признанные мастера в производстве рома трепетно оберегают свои знания и опыт с 1862 года. В ходе длительных экспериментов им удалось облагородить резкий пиратский напиток\r\n\r\nи сделать ром легче, чище и элегантнее, установив в качестве оптимальной формы подачи элегантный шот с долькой апельсина. Столь смелое нововведение позволило в полной мере раскрыть и передать насыщенный вкус и богатый пряный аромат всемирно известного рома.', 'shot', 'Шоты', 'drink_img/shot/b-black-big.png', '1'),
(25, 'Борец', 'Налей в стопку золотую текилу из 100% агавы 50 мл\r         Положи в другую стопку ломтик огурца, посыпанный солью\r         Выпей текилу и заверши ее огурцом\r \r ', 'Автор Хартии о независимости Мексики Хосе Морелос был лидером восстания, которое под его предводительством переросло из бунта в войну за независимость от Испании.\r\n\r\nВ 1828 году город его рождения был переименован в Морелию, в 1869 его именем был назван штат Морелос. Дело же его и полтора века спустя вдохновляет барменов на смелые эксперименты с подачей мексиканской текилы.', 'shot', 'Шоты', 'drink_img/shot/fighter-big.png', '1'),
(26, 'Золотая текила с лаймом и солью', 'Налей в стопку золотую текилу 50 мл\r         Смочи долькой лайма руку между большим и указательным пальцем\r         Посыпь это место солью\r         Лизни соль, выпей шот и заверши его лаймом\r \r ', 'Самый известный способ пить текилу укладывается в 3 действия: лизнул, выпил, закусил.\r\n\r\nЛизнул руку с солью, выпил текилу и закусил лаймом. Мексиканцы рассказывают, что если всё сделать правильно, то после 10 раза ты поймешь, куда исчезли ацтеки!', 'shot', 'Шоты', 'drink_img/shot/gold_tequila_with_lime_and_salt-big.png', '1'),
(27, 'Серебряная текила с лаймом и солью', 'Налей в стопку серебряную текилу 50 мл\r         Смочи долькой лайма руку между большим и указательным пальцем\r         Посыпь это место солью\r         Лизни соль, выпей шот и заверши его лаймом\r \r ', 'Самый известный способ пить текилу укладывается в 3 действия: лизнул, выпил, закусил.\r\n\r\nЛизнул руку с солью, выпил текилу и закусил лаймом. Мексиканцы рассказывают, что если всё сделать правильно, то после 10 раза ты поймешь, куда исчезли ацтеки!', 'shot', 'Шоты', 'drink_img/shot/silver_tequila_with_lime_and_salt-big.png', '1'),
(28, '911', 'Налей в стопку мятный ликер зеленый 20 мл\r         Добавь 2 капли красного соуса табаско\r         Используя коктейльную ложку, уложи слой ликера ко...', 'Многие ну очень профессиональные врачи советуют экстренный шот в качестве лекарства от плохого настроения.\r\n\r\nБудь то непогода или жизненные трудности - 911 всегда придет к тебе на помощь.', 'shot', 'Шоты', 'drink_img/shot/911-big.png', '1'),
(29, 'Абсент без разрешения', 'Налей в стопку яблочный ликер зеленый 20 мл\r         Используя коктейльную ложку, уложи слой айриш крим 20 мл и слой абсента 20 мл\r \r ', 'Во времена второй империи (эпоха правления Наполеона III) абсент становится определяющей чертой парижской жизни.\r\n\r\nВласть в руках буржуазии ознаменовалась повсеместным обычаем пить абсент &laquo;без разрешения&raquo; &mdash; ритуально без спроса заканчивая рабочий день и переходя в вечернее настроение.', 'shot', 'Шоты', 'drink_img/shot/absinthe_without_leave-big.png', '1'),
(30, 'Амиго', 'Налей в стопку кофейный ликер 20 мл\r         Используя коктейльную ложку, уложи слоями: сливки (33%) 10 мл и серебряную текилу 20 мл\r \r ', 'Старинная испанская пословица гласит: &laquo;Настоящий друг тот, кто после пары выпитых коктейлей &laquo;Амиго&raquo;, не задумываясь, подарит тебе iPhone&raquo;.', 'shot', 'Шоты', 'drink_img/shot/amigo-big.png', '1'),
(31, 'Барбадоска', 'Налей в стопку ликер красный грейпфрут 15 мл\r         Используя коктейльную ложку, уложи слоями кокосовый ликер 15 мл и темный ром 15 мл\r \r ', 'Говорят, у девушек, родившихся на острове Барбадос, по венам течёт не кровь, а настоящие зажигательные коктейли!\r\n\r\nЭти красотки могут танцевать без устали и поражать мужчин одним лёгким взмахом ресниц. Хочешь так же? Нет проблем &mdash; пусти барбадосский коктейль и по своим венам!', 'shot', 'Шоты', 'drink_img/shot/barbadian-big.png', '1'),
(32, 'Блекбери шот', 'Налей в стопку черносмородиновый ликер 25 мл\r         Положи две ягоды ежевики\r         Используя коктейльную ложку, уложи слой белого рома 20 мл\r \r ', 'Согласно древней легенде, кровь, пролитая титанами в борьбе с олимпийскими богами, превратилась в ягоды ежевики.\r\n\r\nИ с тех пор уже тысячи лет ежевику собирают для приготовления коктейлей, способных даровать смертным титаническую мощь.', 'shot', 'Шоты', 'drink_img/shot/blackberry_shot-big.png', '1'),
(33, 'Боулинг в ТУ-154', 'Смешай сгущенку 3 ч. ложки с кофе 15 мл и налей в стопку\r         Используя коктейльную ложку, уложи слой водки 15 мл\r \r ', 'Соблюдая технику безапасности полета, экипаж, конечно, никогда не позволит устроить в самолете боулинг-матч.\r\n\r\nХотя протяженность прохода среднего авиалайнера практически совпадает с длиной дорожки для боулинга - 18 м. Но если угостить стюардессу кофейным коктейлем со сгущенкой - о матче она никому не расскажет. Более того, скорее всего она даже погоняет с тобой шары.', 'shot', 'Шоты', 'drink_img/shot/bowling_in_tu-154-big.png', '1'),
(34, 'Боярский', 'Налей в стопку гренадин 25 мл\r         Используя коктейльную ложку, уложи слой водки 25 мл\r         Добавь 4-5 капель красного соуса табаско\r \r ', 'Имя автора легендарного шота затерялось где-то между Владивостоком и Москвой, также как и точное место рождения.\r\n\r\nНо несмотря на темное прошлое, в России не осталось ни одного бара, где бы не знали Боярского. ', 'shot', 'Шоты', 'drink_img/shot/boyarskyi-big.png', '1'),
(35, 'Бухарест', 'Налей в стопку сироп маракуйи 20 мл\r         Используя коктейльную ложку, уложи слой кампари 15 мл и слой белого рома 15 мл\r \r ', '\r\n\r\nБухарест - столица Румынии. А вы что подумали?\r\n', 'shot', 'Шоты', 'drink_img/shot/bucharest-big.png', '1'),
(36, 'Быстрый ванильный шот', 'Налей в стопку водку ванильную 50 мл\r         Посыпь веер из яблока молотой корицей\r         Выпей шот и заверши его яблоком\r \r ', 'Ваниль, которую выращивали голландцы в XIX веке на острове Ява, не приносила плодов 18 лет.\r\n\r\nА как только все легализовали &mdash; сразу же разошлась.', 'shot', 'Шоты', 'drink_img/shot/fast_vanilla_shot-big.png', '1'),
(37, 'Великая стена шутер', 'Налей в стопку водку мандариновую 50 мл\r         Налей в другую стопку черносмородиновый сироп 25 мл и окуни в него очищенную дольку мандарина на шпажке\r         Выпей шот и заверши его мандарином\r \r ', 'Великая китайская стена растянулась по северному Китаю почти на 9000 км.\r\n\r\nРади ее строительства император Цинь Ши-хуанди пожертвовал своими любимыми мандариновыми садами. В память о них, он приказал создать мандариновый напиток, который бы стал крепким как стена, но фруктовым как сады.', 'shot', 'Шоты', 'drink_img/shot/great_wall_shooter-big.png', '1'),
(38, 'Все падают', 'Налей в стопку кофейный ликер 20 мл\r         Используя коктейльную ложку, уложи слоями ликер корицы 20 мл и ром-151 15 мл\r \r ', '&laquo;Вот иду я красивая по улице, а мужчины так и столбенеют.\r\n\r\nА которые послабее, так и падают, падают. И сами собой в штабеля укладываются!&raquo; &mdash; мечтала героиня знаменитого фильма. Хочешь воплотить ее мечты в жизнь? Тогда сбивающий с ног коктейль &mdash; для тебя!', 'shot', 'Шоты', 'drink_img/shot/all_fall_down-big.png', '1'),
(39, 'Гальяно хот шот', 'Налей в стопку гальяно 20 мл\r         Используя коктейльную ложку, уложи слой эспрессо 20 мл и слой сливок (33%) 10 мл\r \r ', 'Джон Гальяно &mdash; один из самых влиятельных создателей мировой моды.\r\n\r\nС тех пор, как он стал главным дизайнером Christiаn Dior, на показах модного дома блистают Николь Кидман, Селин Дион и Деми Мур. &laquo;Что же дарует гению вдохновение?&raquo; &mdash; перешептываются на фуршете после показа восхищенные модники. Не догадываясь, что ответ, возможно, совсем рядом &mdash; в маленьком коктейле!', 'shot', 'Шоты', 'drink_img/shot/galliano_hot_shot-big.png', '1'),
(40, 'Горькая пилюля', 'Налей в стопку сироп маракуйя 20 мл\r         Используя коктейльную ложку, уложи слоями кампари 15 мл и водку 15 мл\r \r ', 'Долгие годы среди жителей римских предместий ходила легенда о лекаре, располагающем средством от проблем и грусти.\r\n\r\nОн каждый раз увещевал своих пациентов, что его лекарство очень горько и может прийтись им не по душе. Но к его дому каждый вечер выстраивалась очередь мечтающих получить горькую пилюлю и забыть о невзгодах на какое-то время.', 'shot', 'Шоты', 'drink_img/shot/bitter_pill-big.png', '1'),
(41, 'Грушевый шот', 'Налей в стопку водку грушевую 50 мл\r         Посыпь веер из долек груши молотой корицей\r         Выпей шот и заверши его грушей\r \r ', 'Впервые упоминания о груше встречаются в древних японских одах, написанных несколько веков назад.\r\n\r\nПод влиянием грушевых шотов особенно хорошо сочиняются хокку. Попробуй и убедись сам!', 'shot', 'Шоты', 'drink_img/shot/pears_shot-big.png', '1'),
(42, 'Джоконда шутер', 'Налей в стопку водку перечную 50 мл\r         Налей в другую стопку сахарный сироп 25 мл и окуни в него клубнику на шпажке\r         Выпей шот и заверши его клубникой\r \r ', 'Одна из самых известных в мире картин &mdash; портрет госпожи Лизы Джокондо, написанный Леонардо да Винчи в XVI веке.\r\n\r\nПод каким углом ни взглянешь на полотно &mdash; девушка всегда смотрит прямо на тебя и загадочно улыбается. Если ты мечтаешь так же покорять одной лишь улыбкой, откроем тебе секрет: выпив коктейль с перцем и клубникой, ты сможешь еще и не такое.', 'shot', 'Шоты', 'drink_img/shot/gioconda_shooter-big.png', '1'),
(43, 'Дочь Боярского', 'Налей в стопку гренадин 25 мл\r\nИспользуя коктейльную ложку, уложи слой самбуки светлой 25 мл\r\nДобавь 4-5 капель красного соуса табаск', 'Имя автора легендарного шота затерялось где-то между Владивостоком и Москвой, также как и точное место рождения.\r\n\r\nНо несмотря на темное прошлое, не осталось ни одного бара, где бы не знали дочь Боярского.', 'shot', 'Шоты', 'drink_img/shot/boyarskyi_daughter-big.png', '1'),
(44, 'Завтрак', 'Налей в стопку кофейный ликер 15 мл\r         Используя коктейльную ложку, аккуратно уложи яичный желток 1 шт и налей слой водки 15 мл\r \r ', 'В ходе последних исследований выяснилось, что самый полезный завтрак &mdash; английский, самый быстрый &mdash; американский, а самый вкусный &mdash; французский.\r\n\r\nА самый неожиданный &mdash; коктейльный, ведь им завтракают, как правило, ещё ночью!', 'shot', 'Шоты', 'drink_img/shot/breakfast-big.png', '1'),
(45, 'Завтрак Джокера', 'Налей в стопку кофейный ликер 20 мл\r         Используя коктейльную ложку, уложи слой коньяка 20 мл\r         Аккуратно налей сверху желток яйца\r \r ', 'Джокер &mdash; главный злодей, антипод Бэтмена.\r\n\r\nЧеловек-клоун с зелеными волосами, белым лицом и постоянной улыбкой ежедневно за завтраком предавался мечтам о том, как подчинит себе весь город. У настоящего злодея и завтрак, и мечты грандиозные!', 'shot', 'Шоты', 'drink_img/shot/jokers_breakfast-big.png', '1'),
(46, 'Краснокожий мачо', 'Налей в стопку лаймовый сироп 20 мл\r         Используя коктейльную ложку, уложи слоями клубничный ликер 20 мл и серебряную текилу 20 мл\r \r ', 'Во многих семьях из поколения в поколение по женской линии передается легенда.\r\n\r\nБудто на одном из испанских пляжей можно встретить краснокожего мачо, способного подарить женщине такое наслаждение, о котором и говорить неприлично. Уже несколько сотен лет каждая женщина хотя бы раз отправляется в Испанию на поиски. Не подозревая, что Краснокожий мачо &mdash; это коктейль.', 'shot', 'Шоты', 'drink_img/shot/red-skinned_macho-big.png', '1'),
(47, 'Красный пес', 'Налей в стопку самбуку светлую 20 мл\r         Используя коктейльную ложку, уложи слоями красный соус табаско 5 мл и серебряную текилу 25 мл\r \r ', 'В Древнем Китае красная собака Тьен-ку считалась символом верности и взаимопонимания.\r\n\r\nПоэтому каждой вновь образовавшейся паре до сих пор рекомендуют в начале отношений почаще пить незаменимый китайский коктейль.', 'shot', 'Шоты', 'drink_img/shot/red_dog-big.png', '1'),
(48, 'Кукарача', 'Налей в стопку ликер манго 15 мл\r         Используя коктейльную ложку, уложи слоями апельсиновый ликер сухой 15 мл и серебряную текилу 15 мл\r \r ', 'Одноименная песня на испанском языке, была очень популярна во времена революции в Мексике начала XX века. Хорошо, что текила популярна всегда!', 'shot', 'Шоты', 'drink_img/shot/cucaracha-big.png', '1'),
(49, 'Лавина', 'Налей в стопку кофейный ликер 20 мл\r         Используя коктейльную ложку, уложи слоями ликер какао светлый 15 мл и южный комфорт 15 мл\r \r ', 'Лавиной называют соскальзывающую со склонов гор снежную массу, скорость которой может достигать 125 м/с.\r\n\r\nЭксперементальным путем было выяснено, что одноименный коктейль уносит примерно с такой же скоростью. ', 'shot', 'Шоты', 'drink_img/shot/avalanche-big.png', '1'),
(50, 'Маленькая соленая собака', 'Налей в стопку грейпфрутовый сок 15 мл и ликер красный грейпфрут 15 мл\r         Размешай коктейльной ложкой\r         Используя коктейльную ложку, уложи слой водки 15 мл\r \r ', 'Техасский покер &mdash; самая популярная разновидность клубного покера, был первой карточной игрой штата Техас. Однажды в штат приехали русские моряки и привезли с собой много водки.\r\n\r\nКовбои предпочитали пить крепкие напитки, смешивая их с грейпфрутовым соком, а моряки, чтобы не терять дух моря, добавили к нему соли. Так получился морской коктейль, воодушевляющий на великие свершения. Игра продолжалась до захода солнца, и, когда завыли койоты, веселые игроки дружно крикнули: &laquo;Эй, бармен! Налей-ка нам еще &quot;Солёной собаки&quot;&raquo;.', 'shot', 'Шоты', 'drink_img/shot/little_salty_dog-big.png', '1'),
(51, 'Малиновая заря', 'Налей в стопку водку малиновую 50 мл\r         Укрась долькой апельсина с ванильным сахарным песком на шпажке\r         Выпей шот и заверши его апельсином\r \r ', 'На закате, когда солнце соприкасается с горизонтом, небо меняет свой цвет.\r\n\r\nЕсли оно становится золотым &mdash; уходит всё плохое, что случилось за день. Если же небо становится красным &mdash; срочно выпей вечерний шот, чтобы небо стало золотым и всё плохое ушло за горизонт!', 'shot', 'Шоты', 'drink_img/shot/raspberry_sunset-big.png', '1'),
(52, 'Маска зла', 'Положи в стопку 1 коктейльную вишню\r         Налей ликер личи 25 мл\r         Используя коктейльную ложку, уложи слой темного рома 25 мл\r \r ', 'Маска зла исторически связана с традицией изгнания злых духов.\r\n\r\nТанцуя вокруг столба, мамбо (колдунья), вместе со своей помощницей унси и помощником ла пласом, струйкой воды из кувшина создают вокруг шеста магический круг в честь Папы Легби и охранителя дома Огуна Феррея, дабы отогнать от себя и присутствующих злых духов.\r\n\r\nУнган или мамбо посыпает пол мукой и чертит веве (символы лоа). Затем обязательна экстатическая пляска (билонго) под звук барабанов. Женщины в белых платьях, а мужчины в костюмах. Когда публика достаточно разогрелась, бокор пускает...и они впадают в транс. Есть способ попроще. Выпей несколько порций и ни один дьявол тебя не тронет! ', 'shot', 'Шоты', 'drink_img/shot/mask_of_evil-big.png', '1'),
(53, 'Мексиканский поцелуй', 'Налей в стопку мультифруктовый сок 25 мл\r         Используя коктейльную ложку, уложи слой серебряной текилы 25 мл\r         Добавь 3-4 капли красного табаско соуса\r \r ', 'В Мексике принято целовать друга друга в щеку при знакомстве, встрече или на прощание.\r\n\r\nДля тех, кто не готов сразу переходить к тактильным ласкам, придумали шот, столь же жгучий, как и поцелуй мексиканца.', 'shot', 'Шоты', 'drink_img/shot/mexican_kiss-big.png', '1'),
(54, 'Мока чоколата яя', 'Налей в стопку кофейный ликер 20 мл\r         Используя коктейльную ложку, уложи слоями амаретто ликер 20 мл и водку 20 мл\r \r ', 'Пей ка Пей ка. Опа опа. Ата та та', 'shot', 'Шоты', 'drink_img/shot/mocha_chocolata_yaya-big.png', '1'),
(55, 'Пирог с курагой', 'Налей в стопку ликер какао светлый 15 мл\r         Используя коктейльную ложку, уложи слоями абрикосовый ликер 15 мл и сливки (11%) 15 мл\r \r ', 'Однажды мама отправила Красную Шапочку отнести бабушке пирожков.\r\n\r\nПроходя через дремучий лес, Красная Шапочка встретила Волка. Он хотел съесть Красную Шапочку, а она предложила ему Пирог с курагой. До бабушки Красная Шапочка в итоге так и не дошла, но зато они с Волком классно повеселились!', 'shot', 'Шоты', 'drink_img/shot/dried_apricots_pie-big.png', '1'),
(56, 'Противная слякоть', 'Налей в стопку кофейный ликер 20 мл\r         Используя коктейльную ложку, уложи слой айриш крим 20 мл и слой абсента 20 мл\r \r ', 'Быть барменом &ndash; это не только умение смешивать хорошие коктейли. Хотя бы раз в неделю в бар зайдет человек, которого просто нужно выслушать.\r\n\r\nНе нужно давать советов или пытаться сожалеть. Умение слушать &mdash; лучшее из качеств, которым может обладать бармен. Именно для таких случаев Тони придумал коктейль &laquo;Противная слякоть&raquo;. Ведь он неоднократно помогал его гостям обрести спокойствие и выйти из бара с улыбкой.', 'shot', 'Шоты', 'drink_img/shot/nasty_slush-big.png', '1'),
(59, 'Виски он зе рокс', 'Наполни рокс кубиками льда доверху\r         Налей виски шотландский 50 мл\r \r ', 'Шотландия и Ирландия ведут непримиримую борьбу за право называться родиной виски.\r\n\r\nОфициальным годом появления шотландского виски считается 1494 год. А в Ирландии верят, что сам Святой Патрик изобрёл виски ещё в V веке. Так или иначе, пока производители спорят, потребители наслаждаются.', 'lounge', 'Лаунж', 'drink_img/lounge/whisky_on_the_rocks-big.png', '1'),
(60, 'Древнегреческий', 'Налей в бокал для вина красное вино сухое 100 мл\r\n        Аккуратно разбавь водой без газа 50 мл\r\n\r\n', 'Это сегодня на многих симпозиумах люди часто засыпают. Древнегреческий обычай &mdash; симпозиум &mdash; являл собой главную часть вечера благородных греков.\r\n\r\nСамый почетный гость &mdash; симпозиарх, определял в какой пропорции стоит\r\n\r\nразбавлять в этот вечер вино и сколько коктейлей выпьет каждый за столом.\r\n\r\nВ зависимости от первого и второго решения греки либо танцевали сиртаки, либо ложились спать!', 'lounge', 'Лаунж', 'drink_img/lounge/ancient_greek-big.png', '1'),
(61, 'Содовая с огурцом', 'Положи в охлажденный слинг ломтик огурца\r\n        Налей содовую доверху\r\n\r\n', 'Долгое время оставалось загадкой, что собой представляет живая вода, о которой сложено столько легенд.\r\n\r\nНаконец стало достоверно известно: обычный огурец содержит настолько уникальные вещества и минеральные соли, что если поместить его в воду, получится коктейль, обладающий поистине сказочными свойствами.', 'lounge', 'Лаунж', 'drink_img/lounge/soda_and_cucumber-big.png', '0'),
(62, 'Айриш крим со льдом', 'Наполни рокс кубиками льда доверху\r\n        Налей айриш крим наполовину\r\n        Укрась стеблем лимонной травы, порезанной клубникой, ягодами ежевики и голубики\r\n\r\n', 'Казалось, влюблённый в Снежную королеву страж испробовал все способы, чтобы обратить на себя внимание ледяной красавицы.\r\n\r\nОн вычесывал её северных оленей, полировал её ледяной трон, однако все его усилия оставались незамеченными. Но когда он преподнёс Снежной королеве ледяной коктейль, даже её холодное сердце жарко затрепетало.', 'lounge', 'Лаунж', 'drink_img/lounge/irish_cream_with_ice-big.png', '1'),
(63, 'Водка он зе рокс', 'Наполни рокс кубиками льда доверху\r\n        Налей водку 50 мл\r\n        Укрась цедрой лимона\r\n\r\n', 'Во время правления Екатерины II водка считалась самым элитарным напитком в мире.\r\n\r\nТогда её подавали в хрустальных графинах и пили стопками. С тех пор прошло 3 века, а уважение к самому чистому алкоголю только растёт. И доросло до специальной подачи в роксах со льдом.', 'lounge', 'Лаунж', 'drink_img/lounge/vodka_on_the_rocks-big.png', '1'),
(64, 'Яблочно-апельсиновый фреш', 'Положи в соковыжималку очищенный и порезанный апельсин 1 шт и порезанное зеленое яблоко 1 шт\r\n        Сделай сок\r\n        Перелей в охлажденный хайбол\r\n        Укрась дольками апельсина\r\n\r\n', 'Врачи утверждают, что яблочно-апельсиновый фреш помогает бросить курить, поэтому его используют в антиникотиновых диетах.\r\n\r\nСвежевыжатый апельсиновый сок поднимает настроение, борется с синдромом хронической усталости и так же как и яблочный &mdash; укрепляет иммунитет. Фруктовые соки очищают организм и способствуют обмену веществ. Фреши следует пить сразу после приготовления и за полчаса до еды.', 'lounge', 'Лаунж', 'drink_img/lounge/apple-orange_fresh-big.png', '0'),
(65, 'Ананасово-гранатовый фреш', 'Очисть от кожуры половинку ананаса, разрежь ее на кружочки и вырежи из них сердцевину\r\n        Положи в соковыжималку очищенную половину ананаса и сделай сок\r\n        С помощью пресса для граната выжми сок двух гранатов\r\n        Смешай оба сока и перелей в охлажденный хайбол\r\n        Укрась листом ананаса\r\n\r\n', 'Яркий фреш из ананаса и граната на завтрак зарядит тебя бодростью на целый день.\r\n\r\nАнанасовый сок используется в диетах, благодаря содержанию бромелайна &mdash; естественного расщепителя жира. Гранатовый сок как никакой другой умеет повышать гемоглобин &mdash; пигмент крови, доставляющий кислород в ткани организма. Фруктовые соки очищают организм и способствуют обмену веществ. Фреши следует пить сразу после приготовления и за полчаса до еды.', 'lounge', 'Лаунж', 'drink_img/lounge/pineapple-pomegranate_fresh-big.png', '0'),
(66, 'Гранатово-грейпфрутовый фреш', 'С помощью пресса для граната выжми сок двух гранатов и одного грейпфрута\r\n        Смешай оба сока и перелей в охлажденный хайбол\r\n\r\n', 'Гранатово-грейпфрутовый фреш укрепляет иммунитет и помогает противостоять простудам.\r\n\r\nГрейпфрутовый сок особенно полезен при повышенных умственных нагрузках. Он бодрит и избавляет от усталости. Гранатовый сок повышает гемоглобин. Фруктовые соки очищают организм и способствуют обмену веществ. Фреши следует пить сразу после приготовления и за полчаса до еды.', 'lounge', 'Лаунж', 'drink_img/lounge/pomegranate-grapefruit_fresh-big.png', '0'),
(67, 'Морковно-грушевый фреш', 'Положи в соковыжималку порезанные груши 2 шт и очищенную и порезанную морковь 1,5 шт\r\n        Сделай сок и перелей его в охлажденный хайбол\r\n        Укрась веером из груши на шпажке\r\n\r\n', 'Как утверждают косметологи, морковно-грушевый фреш благотворно влияет на цвет лица.\r\n\r\nГрушевый сок рекомендован людям, у которых проблемы с почками. Морковный &mdash; полезен для глаз, укрепляет зубы и улучшает аппетит. Овощные соки быстро восстанавливают силы, а фруктовые &mdash; очищают организм и способствуют обмену веществ. Свежевыжатые соки следует пить сразу после приготовления и за полчаса до еды.', 'lounge', 'Лаунж', 'drink_img/lounge/carrot-pear_fresh-big.png', '0'),
(68, 'Томатный фреш с петрушкой', 'Положи в соковыжималку порезанные помидоры 2,5 шт и большой пучок петрушки\r\n        Сделай сок и перелей его в охлажденный хайбол\r\n        Укрась помидорами черри на шпажке\r\n\r\n', 'Свежевыжатый томатный сок низкокалориен и полезен беременным и кормящим женщинам.\r\n\r\nДобавление соли снижает полезные свойства сока, поэтому вместо приправ лучше добавить свежую зелень. Овощные соки быстро восстанавливают силы. Свежевыжатые соки следует пить сразу после приготовления и за полчаса до еды.', 'lounge', 'Лаунж', 'drink_img/lounge/tomato_fresh_with_parsley-big.png', '0');
INSERT INTO `drinks` (`id`, `drink_title`, `drink_recipe`, `drink_history`, `drink_type_en`, `drink_type_rus`, `drink_img`, `is_alco`) VALUES
(69, 'Ржавый гвоздь', 'Наполни рокс кубиками льда доверху\r\n        Налей драмбуи 25 мл и виски шотландский 50 мл\r\n        Размешай коктейльной ложкой\r\n\r\n', 'В Америке напитки, где крепкий алкоголь смешивается с ликером в стакане со льдом получили имя &laquo;дуэт&raquo;.\r\n\r\nНазвание дуэта Rusty Nail (что в переводе &laquo;Ржавый гвоздь&raquo;) впервые появилось в New York Times 1963 года. Коктейльный &laquo;гвоздь&raquo; стал гвоздём программы в меню знаменитых Rat Pack, куда входили Синатра, Дин Мартин и Сэм Дэвис младший. ', 'lounge', 'Лаунж', 'drink_img/lounge/rusty_nail-big.png', '1'),
(70, 'Цитрусовый Кейп Кодер', 'Наполни хайбол кубиками льда доверху\r\n        Налей водку цитрон 50 мл и клюквенный морс доверху\r\n        Размешай коктейльной ложкой\r\n        Укрась кружками лимона\r\n\r\n', 'Дейл де Грофф, известный как &laquo;Король коктейля&raquo; прославил Космополитен, который стал олицетворением стиля жизни Верхнего Ист-сайда во всем мире.\r\n\r\nВ бешеном ритме современного мира сложные сочетания сменяются простыми, и не важно, где ты танцуешь в компании Цитрусового Кейп Кодера, дух Манхэттена всегда с тобой. ', 'lounge', 'Лаунж', 'drink_img/lounge/tsitrusovyy_keyp_koder-big.png', '1'),
(71, 'Черный русский', 'Наполни рокс кубиками льда доверху\r\n        Налей водку 50 мл и кофейный ликер 25 мл\r\n        Размешай коктейльной ложкой\r\n\r\n', 'В период &laquo;холодной войны&raquo; между СССР и США американская светская львица Пёрл Места устраивала роскошные вечеринки с участием ведущих журналистов, актеров и политиков.\r\n\r\nНа одном из ее мероприятий в Брюсселе в 1949 году бармен отеля &laquo;Метрополь&raquo; Густаве Топс приготовил для Пёрл новый коктейль, дав ему актуальное название. Коктейль стал очень популярен в 1960-70 годы и породил множество вариантов.', 'lounge', 'Лаунж', 'drink_img/lounge/black_russian-big.png', '1'),
(72, 'Яблочно-сельдереевый фреш с имбирем', 'Положи в соковыжималку: порезанное зеленое яблоко 1 шт, 5 стеблей сельдерея и корень имбиря 2 дольки\r\n        Сделай сок и перелей его в охлажденный хайбол\r\n        Укрась стеблем сельдерея\r\n\r\n', 'Яблочно-сельдереевый сок с добавлением имбиря &mdash; находка для тех, кто мечтает похудеть.\r\n\r\nОн выводит из организма лишнюю жидкость и способствует исчезновению сантиметров на талии. Овощные соки быстро восстанавливают силы, а фруктовые &mdash; очищают организм и стимулируют обмен веществ. Свежевыжатые соки следует пить сразу после приготовления и за полчаса до еды.', 'lounge', 'Лаунж', 'drink_img/lounge/apple-celery_fresh_with_ginger-big.png', '0'),
(73, 'Бурбон кола', 'Наполни хайбол кубиками льда доверху\r\n        Налей бурбон 50 мл и колу доверху\r\n        Аккуратно размешай коктейльной ложкой\r\n\r\n', 'Американская версия виски кола\r\n\r\nДля тех у кого оружие до пола!\r\n', 'lounge', 'Лаунж', 'drink_img/lounge/bourbon_cola-big.png', '1'),
(74, 'Виски кола', 'Наполни хайбол кубиками льда доверху\r\n        Налей виски шотландский 50 мл и колу доверху\r\n        Аккуратно размешай коктейльной ложкой\r\n\r\n', 'Говорят, что у самого известного в мире коктейля нет ни одной легенды. Ну и что!', 'lounge', 'Лаунж', 'drink_img/lounge/whisky_cola-big.png', '1'),
(75, 'Грейпфрут сплеш', 'Наполни рокс дробленым льдом доверху\r\n        Налей джин 20 мл и ликер красный грейпфрут 40 мл\r\n        Размешай коктейльной ложкой\r\n        Досыпь немного дробленого льда\r\n        Укрась треугольничком грейпфрута\r\n\r\n', 'Грейпфрут впервые был обнаружен на Барбадосе в XVIII веке и с тех пор известен как одно из семи чудес острова.\r\n\r\nОстальные шесть &mdash; это грейпфрутовые коктейли. Их вкусовые всплески оказались настолько неповторимы, что стали официальными барбадосскими символами.', 'lounge', 'Лаунж', 'drink_img/lounge/grapefruit_splash-big.png', '1'),
(76, 'Ежевичный милкшейк', 'Положи в блендер ежевику 6 ягод и ванильное мороженое 200 г\r\n        Налей молоко (3%) 80 мл\r\n        Взбей без добавления льда и перелей в хайбол\r\n        Укрась ягодой ежевики\r\n\r\n', 'Все мы когда-то были детьми. Возможно, ими и остались. Говорят, коктейль придумали друиды, а, может, и Дед Мороз. Одно остается явным: милкшейки любят все.', 'lounge', 'Лаунж', 'drink_img/lounge/blackberry_milkshake-big.png', '0'),
(77, 'Малиновый милкшейк', 'Положи в блендер малину 10 ягод и ванильное мороженое 200 г\r\n        Налей молоко (3%) 80 мл\r\n        Взбей без добавления льда и перелей в хайбол\r\n        Укрась ягодкой малины\r\n\r\n', 'Все мы когда-то были детьми. Возможно, ими и остались. Говорят, коктейль придумали друиды, а, может, и Дед Мороз. Одно остается явным: милкшейки любят все.', 'lounge', 'Лаунж', 'drink_img/lounge/raspberry_milkshake-big.png', '0'),
(78, 'Ром с вишневым соком', 'Наполни хайбол кубиками льда доверху\r\n        Налей белый ром 50 мл и вишневый сок доверху\r\n        Размешай коктейльной ложкой\r\n        Укрась кружком апельсина\r\n\r\n', 'Возьми с собой вишневый сок и ром\r\n\r\nКогда идешь гулять по городу вдвоем!\r\n', 'lounge', 'Лаунж', 'drink_img/lounge/cherry_white_rum-big.png', '1'),
(79, 'Ром с яблочным соком', 'Наполни хайбол кубиками льда доверху\r\n        Налей белый ром 50 мл и яблочный сок доверху\r\n        Размешай коктейльной ложкой\r\n        Укрась веером из яблок\r\n\r\n', 'Яблочный сок и рома белый цвет\r\n\r\nТебе подскажут правильный ответ!\r\n', 'lounge', 'Лаунж', 'drink_img/lounge/apple_white_rum-big.png', '1'),
(80, 'Яблочно-морковный фреш с имбирем', 'Положи в соковыжималку: порезанные зеленые яблоки 2 шт, небольшую очищенную и порезанную морковь 1 шт и корень имбиря 3 дольки\r\n        Сделай сок и перелей его в охлажденный хайбол\r\n        Укрась дольками яблока\r\n\r\n', 'По мнению диетологов, яблочно-морковный фреш с добавлением имбиря заряжает энергией и оптимизмом.\r\n\r\nОвощные соки быстро восстанавливают силы, а фруктовые &mdash; очищают организм и способствуют обмену веществ. Свежевыжатые соки следует пить сразу после приготовления и за полчаса до еды.', 'lounge', 'Лаунж', 'drink_img/lounge/apple-carrot_fresh_with_ginger-big.png', '0'),
(81, 'Вергилий', 'Наполни рокс кубиками льда доверху\r\n        Налей в стакан для смешивания самбуку апельсин-манго 40 мл и лимонный биттер 2 капли\r\n        Наполни стакан кубиками льда и размешай коктейльной ложкой\r\n        Перелей через стрейнер в рокс\r\n        Укрась цедрой апельсина\r\n\r\n', 'Римский поэт Публий Вергилий Марон жил в 1 в. до н. э. и стал одним из наиболее значительных поэтов того времени, создав новый тип эпической поэмы.\r\n\r\nЕще будучи юношей он получил тогу зрелости и заслужил уважение современников. Почтенные мужи не уставали поднимать бокалы с коктейлями в честь молодого поэта, надеясь, что за это часть его таланта перейдет к ним самим.', 'lounge', 'Лаунж', 'drink_img/lounge/vergilius-big.png', '1'),
(82, 'Горящий доктор Пеппер', 'Налей в хайбол пиво светлое на 2/3\r\n        Налей в стопку амаретто ликер 25 мл\r\n        Используя коктейльную ложку, уложи слой рома-151 25 мл\r\n        Подожги и опусти стопку в хайбол с пивом\r\n\r\n', 'Знаменитую на весь мир вишневую газировку Dr. Pepper начали выпускать в 1885 году.\r\n\r\nПо одной из версий, Горящий доктор Пеппер был придуман в университетском городке College Station штата Техас в 60-е. По другой &mdash; в новоорлеанском Gold Mine Saloon. Так или иначе, коктейль стал настолько популярным, что в 1991 году попал в один из эпизодов мультсериала &laquo;Симпсоны&raquo;.', 'lounge', 'Лаунж', 'drink_img/lounge/flaming_dr._pepper-big.png', '1'),
(83, 'Оранж биттер', 'Наполни рокс кубиками льда доверху\r\n        Налей кампари 50 мл и красный вермут 50 мл\r\n        Размешай коктейльной ложкой\r\n        Укрась цедрой апельсина\r\n\r\n', 'Однажды принцесса страны Оранж полюбила красавца Великана.\r\n\r\nНо суровый король выслал возлюбленного дочери за 93 моря. Несчастная девушка плакала день и ночь, тоскуя о любимом! А потом выпила коктейль и забила.', 'lounge', 'Лаунж', 'drink_img/lounge/orange_bitter-big.png', '1'),
(84, 'Е-сенча-Л', 'Налей в стакан для смешивания холодный джин 50 мл и холодный чай сенча 50 мл\r\n        Размешай коктейльной ложкой без добавления льда\r\n        Перелей в чашку\r\n        Укрась цедрой грейпфрута и подай на блюдечке с чаем сенча\r\n\r\n', 'Как гласит легенда, японский чай сенча появился в Англии во времена правления Елизаветы I.\r\n\r\nНапиток был преподнесен королеве императором Ёдзеем II на балу по случаю ее дня рождения и тот час был добавлен в джин, которым угощали гостей. Получившийся коктейль назвали в честь двух монархов и по сей день подают в Англии на балах в память о зародившейся дружбе.', 'lounge', 'Лаунж', 'drink_img/lounge/e-sencha-l-big.png', '1'),
(85, 'Монако', 'Налей в охлажденный слинг сироп пина-колада 30 мл и пиво светлое доверху\r\n        Выжми сок лайма 2 ч. ложки\r\n        Аккуратно размешай коктейльной ложкой\r\n        Укрась кусочком ананаса и физалисом на шпажке\r\n\r\n', 'Национальный оркестр Монако в несколько раз больше, чем состав его вооруженных сил.\r\n\r\nСтоит ли удивляться, что конфликтам и войнам граждане этого карликового государства на средиземноморском побережье предпочитают музыку и коктейли. Благодаря чему всегда пребывают в прекрасном настроении!', 'lounge', 'Лаунж', 'drink_img/lounge/monaco-big.png', '1'),
(86, 'Американо', 'Наполни хайбол кубиками льда доверху\r\n        Налей красный вермут 50 мл, кампари 50 мл и содовую доверху\r\n        Аккуратно размешай коктейльной ложкой\r\n        Укрась долькой апельсина\r\n\r\n', 'В 1860 году Гаспаре Кампари начал подавать в своем кафе коктейль &laquo;Милано-Турино&raquo; из биттера, вермута и содовой.\r\n\r\nКогда в 1920-х годах в США вступил в силу запрет на алкоголь, итальянский коктейль стал настоящим хитом среди американских туристов и тут же был переименован из &laquo;Милано&raquo; в &laquo;Американо&raquo;. А в 1928 году его рецепт появился в справочнике Адольфа Торелли &laquo;900 рецептов американских коктейлей и напитков&raquo;.', 'lounge', 'Лаунж', 'drink_img/lounge/americano-big.png', '1'),
(87, 'Белый русский', 'Наполни рокс кубиками льда доверху\r\n        Налей: водку 30 мл, кофейный ликер 30 мл и сливки (11%) 30 мл\r\n        Размешай коктейльной ложкой\r\n\r\n', 'Первые рецептуры c водкой, ликером какао и сливками встречаются в коктейльных справочниках 1930-х годов под названием &laquo;Russian&raquo;.\r\n\r\nВ 80-х годах &laquo;Белый русский&raquo; стал хитом дискотек. Позднее, в 1998 году, фильм братьев Коэнов &laquo;Большой Лебовски&raquo; сделал коктейль культовым. Истинные ценители смешивают и выпивают &laquo;белячка&raquo; синхронно с главным героем, утверждая, что только так можно по-настоящему прочувствовать фильм.', 'lounge', 'Лаунж', 'drink_img/lounge/white_russian-big.png', '1'),
(88, 'Дынно-базиличный смузи', 'Срежь с небольшой дыни верхушку чуть выше половины и извлеки косточки\r\n        Положи в блендер мякоть дыни, шарик сливочного мороженого и базилик зеленый 4 листика\r\n        Добавь в блендер немного льда и взбей\r\n        Перелей в дыню\r\n        Укрась веточкой базилика\r\n\r\n', 'Название измельченных в блендере ягод и фруктов произошло от английского &laquo;smooth&raquo; &mdash; однородный.\r\n\r\nАвторство этой &laquo;жидкой еды&raquo; приписывается Стиву Кахнау, который в начале 1970-х смешал в блендере фрукты, ягоды и орехи с добавлением йогурта и льда. Очень скоро смузи стали хитом среди тех, кто сидит на диете и любимым десертом коктейльных экспериментаторов.', 'lounge', 'Лаунж', 'drink_img/lounge/melon-basil_smoothie-big.png', '0'),
(89, 'Милано спритцер', 'Наполни бокал для вина кубиками льда доверху\r\n        Налей: белое вино сухое 100 мл, кампари 20 мл и содовую 50 мл\r\n        Аккуратно размешай коктейльной ложкой\r\n        Укрась цедрой апельсина\r\n\r\n', 'Сочетание вина с содовой, создает спритцер &ndash; легкий напиток, позволяющий освежиться, но не опьянеть.\r\n\r\nЕго название произошло от немецкого слова &laquo;spritzig&raquo;, что переводится как &laquo;игристый&raquo; и &laquo;живой&raquo;. Модная версия коктейля была придумана в Милане еще в 1865 году в честь основания первого торгового центра в мире - галереи Виктора Эммануила II на площади Дуомо.', 'lounge', 'Лаунж', 'drink_img/lounge/milano_spritzer-big.png', '1'),
(90, 'Негрони', 'Наполни рокс кубиками льда доверху\r\n        Налей: джин 30 мл, красный вермут 30 мл и кампари 30 мл\r\n        Размешай коктейльной ложкой\r\n        Укрась кружком апельсина\r\n\r\n', 'Получив военное образование, граф Камилло Негрони отправился из Флоренции в Америку, где пристрастился к джину.\r\n\r\nВ 1919 году, с введением сухого закона, граф был вынужден вернуться на родину, где бармен его любимого кафе готовил для него &laquo;Американо&raquo; с добавлением джина. Первое упоминание о ставшем популярным новом коктейле появилось в 1947 году и &laquo;Негрони&raquo; стал национальной гордостью итальянцев.', 'lounge', 'Лаунж', 'drink_img/lounge/negroni-big.png', '1'),
(91, 'Ананасовый детокс', 'Положи в блендер очищенный и порезанный ананас 1/2 шт.\r\n        Добавь в блендер немного льда и взбей\r\n        Перелей в слинг\r\n        Укрась веточкой мяты\r\n\r\n', 'Робинзон Крузо 28 лет жил на необитаемом острове, придумывал коктейли и угощал ими Пятницу.\r\n\r\nСамым удачным оказался коктейль из ананаса, после которого Пятница даже заговорил. Долгое время его рецепт держался в тайне, но, к счастью, сегодня коктейль решили воссоздать и усовершенствовать.', 'lounge', 'Лаунж', 'drink_img/lounge/pineapple_detox-big.png', '0'),
(92, 'Белый мексиканец', 'Налей в рокс шоколадную текилу 30 мл, кофейный ликер 25 мл и сливки средней жирности 25 мл\r\n        Наполни бокал кубиками льда и размешивай коктейльной ложкой пока не замерзнут стенки бокала\r\n        Укрась свежемолотым мускатным орехом\r\n\r\n', 'Открытие шоколада, история которого насчитывает около двух тысяч лет, приписывают племени древних Ольмеков.\r\n\r\nВместо того, чтобы переводить дорогие какао бобы на привычный сегодня шоколад, они готовили из них напиток, который считался чудодейственным и помогал видеть будущее.', 'lounge', 'Лаунж', 'drink_img/lounge/white_mexican-big.png', '1'),
(93, 'Бьянко бриз', 'Наполни хайбол кубиками льда доверху\r\n        Налей: белый вермут 50 мл, грейпфрутовый сок 50 мл и клюквенный морс доверху\r\n        Размешай и укрась долькой грейпфрута\r\n\r\n', 'Коктейли-&laquo;бризы&raquo; являются вариацией классического коктейля Морской бриз.\r\n\r\nПервые упоминания о коктейле относятся к концу 1920х годов.\r\n\r\nОднако с тех пор рецепт напитка несколько изменился, ведь изначально для приготовления использовались джин и гренадин. Но став более простым в приготовлении, коктейль обрел еще большую популярность.', 'lounge', 'Лаунж', 'drink_img/lounge/bianco_breeze-big.png', '1'),
(94, 'Бьянко санрайз', 'Наполни хайбол кубиками льда доверху\r\n        Налей: белый вермут 50 мл, апельсиновый сок 75 мл и клюквенный морс 75 мл\r\n        Размешай коктейльной ложкой\r\n        Укрась двумя дольками апельсина\r\n\r\n', 'Есть клюква апельсин и лед\r\n\r\nДобавим бьянко и вперед!\r\n', 'lounge', 'Лаунж', 'drink_img/lounge/bianco_sunrise-big.png', '1'),
(95, 'Ванильный бриз', 'Наполни хайбол кубиками льда доверху\r\n        Налей: водку ванильную 50 мл, клюквенный морс 50 мл и грейпфрутовый сок доверху\r\n        Размешай коктейльной ложкой\r\n        Укрась порезанным грейпфрутом\r\n\r\n', 'Коктейли-&laquo;бризы&raquo; являются вариацией классического коктейля Морской бриз.\r\n\r\nПервые упоминания о коктейле относятся к концу 1920х годов.\r\n\r\nОднако с тех пор рецепт напитка несколько изменился, ведь изначально для приготовления использовались джин и гренадин. Но став более простым в приготовлении, коктейль обрел еще большую популярность.', 'lounge', 'Лаунж', 'drink_img/lounge/vanilla_breeze-big.png', '1'),
(96, 'Водка биттер мед', 'Наполни хайбол кубиками льда доверху\r\n        Налей в хайбол: медовый сироп 10 мл, водку 50 мл и биттер лемон 150 мл\r\n        Аккуратно размешай коктейльной ложкой\r\n        Укрась долькой грейпфрута', 'Биттер лемон, водка, мед\r\n\r\nИ неважно, кто поймет!\r\n', 'lounge', 'Лаунж', 'drink_img/lounge/vodka_bitter_honey-big.png', '1'),
(97, 'Глубинная секс-бомба', 'Налей в хайбол пиво светлое на 2/3\r\n        Налей в стопку мятный ликер зеленый 15 мл\r\n        Используя коктейльную ложку, уложи слоями ликер маракуйя 15 мл и апельсиновый ликер 15 мл\r\n        Подожги и опусти стопку в хайбол с пивом\r\n\r\n', 'Первая подводная лодка появилась во время Первой мировой войны.\r\n\r\nКогда военные поняли, насколько эта штука опасна &mdash; появились и первые глубинные бомбы. Ну, а в мирное время подводники спасаются от долгого заточения глубинными секс-бомбами. И, говорят, совсем не скучают!', 'lounge', 'Лаунж', 'drink_img/lounge/depth_charge_sex-bomb-big.png', '1'),
(98, 'Голубая лагуна', 'Наполни харрикейн кубиками льда доверху\r\n        Налей водку 50 мл, ликер блю курасао 20 мл и спрайт доверху\r\n        Аккуратно размешай коктейльной ложкой\r\n        Укрась долькой ананаса\r\n\r\n', 'Создан сыном хозяина парижского Херриз бара, Гери Маккелона, Энди Маккелоном в 1960 году.', 'lounge', 'Лаунж', 'drink_img/lounge/blue_lagoon-big.png', '1'),
(99, 'Запеченный смузи', 'Очисть и порежь банан 1 шт и 2 кружочка ананаса\r\n        Запеки их в горячей духовке без масла в течение 10 минут\r\n        Положи в блендер очищенный и порезанный апельсин 1 шт и запеченные фрукты\r\n        Добавь в блендер немного льда и тщательно взбей\r\n        Перелей в охлажденный хайбол\r\n        Укрась кусочками ананаса и банана\r\n\r\n', 'Название измельченных в блендере ягод и фруктов произошло от английского &laquo;smooth&raquo; &mdash; однородный.\r\n\r\nАвторство этой &laquo;жидкой еды&raquo; приписывается Стиву Кахнау, который в начале 1970-х смешал в блендере фрукты, ягоды и орехи с добавлением йогурта и льда. Очень скоро смузи стали хитом среди тех, кто сидит на диете и любимым десертом коктейльных экспериментаторов.', 'lounge', 'Лаунж', 'drink_img/lounge/baked_smoothie-big.png', '0'),
(100, 'Кембридж', 'Наполни хайбол кубиками льда доверху\r\n        Налей: джин 50 мл, имбирный эль 30 мл и яблочный сок доверху\r\n        Аккуратно размешай коктейльной ложкой\r\n        Укрась долькой зеленого яблока\r\n\r\n', 'Впервые готовить коктейли с джином начали еще в XVII веке на востоке Англии, в Кембридже.\r\n\r\nЖителям города джин с яблоком и имбирем пришелся особенно по вкусу. Говорят, именно благодаря этому коктейльному сочетанию из стен Кембриджа вышло 83 Нобелевских лауреата.', 'lounge', 'Лаунж', 'drink_img/lounge/cambridge-big.png', '1'),
(101, 'Клубничный милкшейк', 'Положи в блендер клубнику 6 ягод и ванильное мороженое 200 г\r\n        Налей молоко (3%) 80 мл и сироп клубничная конфета 20 мл\r\n        Взбей без добавления льда и перелей в хайбол\r\n        Укрась ягодами клубники\r\n\r\n', 'Все мы когда-то были детьми. Возможно, ими и остались. Говорят, коктейль придумали друиды, а может, и Дед Мороз. Одно остается явным: милкшейки любят все.', 'lounge', 'Лаунж', 'drink_img/lounge/strawberry_milkshake-big.png', '0'),
(102, 'Кокосовый бриз', 'Наполни хайбол кубиками льда доверху\r\n        Налей: малибу 50 мл, ананасовый сок 75 мл и клюквенный морс 75 мл\r\n        Размешай коктейльной ложкой и укрась долькой лайма\r\n\r\n', 'Коктейли-&laquo;бризы&raquo; являются вариацией классического коктейля Морской бриз.\r\n\r\nПервые упоминания о коктейле относятся к концу 1920х годов.\r\n\r\nОднако с тех пор рецепт напитка несколько изменился, ведь изначально для приготовления использовались джин и гренадин. Но став более простым в приготовлении, коктейль обрел еще большую популярность.', 'lounge', 'Лаунж', 'drink_img/lounge/coconut_breeze-big.png', '1'),
(103, 'Лошадиная шея', 'Наполни хайбол кубиками льда доверху\r\n        Налей бурбон 50 мл и имбирный эль доверху\r\n        Добавь ангостуру биттер 1 дэш\r\n        Аккуратно размешай коктейльной ложкой\r\n        Укрась длинной цедрой лимона\r\n\r\n', 'Изначально коктейль, входивший в состав рецептурных справочников еще в конце XIX века, был безалкогольным напитком из кожуры лимона, льда и имбирного эля.\r\n\r\nЛишь в 1910 году в его состав начали добавлять алкоголь. Но во все времена бокал с коктейлем неизменно укарашался длинной лимонной цедрой, схожей с силуэтом шеи лошади. Это и послужило поводом для его названия.', 'lounge', 'Лаунж', 'drink_img/lounge/horse_s_neck-big.png', '1'),
(104, 'Мандариновая дива', 'Наполни хайбол кубиками льда доверху\r\n        Налей: водку мандариновую 50 мл, ананасовый сок 75 мл и спрайт 75 мл\r\n        Аккуратно размешай коктейльной ложкой\r\n        Укрась кружком ананаса\r\n\r\n', 'Дива, она же примадонна, она же королева, она же поп-звезда.\r\n\r\nНеровно дышит к мандариновым коктейлям и с легкостью берет верхнюю &laquo;ля&raquo; при любом удобном случае.', 'lounge', 'Лаунж', 'drink_img/lounge/mandrin_diva-big.png', '1'),
(105, 'Морской бриз', 'Наполни хайбол кубиками льда доверху\r\n        Налей: водку 50 мл, клюквенный морс 100 мл и грейпфрутовый сок 50 мл\r\n        Размешай коктейльной ложкой\r\n        Укрась двумя дольками грейпфрута\r\n\r\n', 'Первые упоминания о коктейле относятся к концу 1920х годов.\r\n\r\nОднако с тех пор рецепт напитка несколько изменился, ведь изначально для приготовления использовались джин и гренадин. Но, став более простым в приготовлении, коктейль обрел еще большую популярность.', 'lounge', 'Лаунж', 'drink_img/lounge/sea_breeze-big.png', '1'),
(106, 'Мульти смэш', 'Наполни хайбол дробленым льдом доверху\r\n        Налей в хайбол: белый ром 50 мл, сахарный сироп 20 мл и мультифруктовый сок 50 мл\r\n        Размешай коктейльной ложкой\r\n        Досыпь немного дробленого льда\r\n        Укрась дольками лимона, лайма и грейпфрута\r\n\r\n', 'А ты знаешь благодаря чему кролик Роджер прославился на весь мир и получил 4 премии Оскар?\r\n\r\nБлагодаря коктейлю, который делает мультяшек веселыми и жизнерадостными. Хочешь настоящего веселья? Выпей мульти-коктейль!', 'lounge', 'Лаунж', 'drink_img/lounge/multi_smash-big.png', '1'),
(107, 'Палома лайт', 'Наполни хайбол кубиками льда доверху\r\n        Налей: золотую текилу 50 мл, грейпфрутовый сок 75 мл и спрайт 75 мл\r\n        Аккуратно размешай коктейльной ложкой\r\n        Укрась половинкой кружка лайма\r\n\r\n', 'Неслучайно мексиканские влюбленные празднуют помолвку именно этим коктейлем.\r\n\r\nВ переводе с испанского Paloma означает &laquo;голубка&raquo;. Существует поверье, будто любовь, соединившая два сердца, подобно голубке будет вечно кружить подле влюбленных, если они смогут вовремя приручить ее, выпив по коктейлю.', 'lounge', 'Лаунж', 'drink_img/lounge/paloma_light-big.png', '1'),
(108, 'Розе с вишней', 'Наполни хайбол кубиками льда доверху\r\n        Налей в хайбол: амаретто ликер 10 мл, розовый вермут 50 мл и вишневый сок 150 мл\r\n        Размешай и укрась коктейльной вишней\r\n\r\n', 'В I веке до н. э. Понтийское царство на весь мир славилось вишневым коктейлем.\r\n\r\nРимский полководец Лукул неоднократно отправлял послов к царю Митридату, с целью выяснить секрет приготовления удивительного напитка, но те всегда возвращались ни с чем. Тогда Лукул завоевал царство и привез в Рим понтийского придворного бармена и вишневое дерево вместе с плодами.', 'lounge', 'Лаунж', 'drink_img/lounge/rose_with_cherry-big.png', '1'),
(109, 'Розовый ром', 'Наполни хайбол кубиками льда доверху\r\n        Налей в хайбол: белый ром 50 мл, клюквенный морс 75 мл и ананасовый сок 75 мл\r\n        Размешай коктейльной ложкой\r\n        Укрась четырьмя ягодами малины\r\n\r\n', 'Клюква, ананас и белый ром\r\n\r\nСегодня будешь диско-королем!\r\n', 'lounge', 'Лаунж', 'drink_img/lounge/pink_rum-big.png', '1');
