INSERT INTO `ingridients` (`id`, `ing_title`, `ing_desc`, `ing_type`, `ing_img`, `ing_type_rus`, `is_ing`) VALUES
(9, 'Лед в кубиках', 'Бар безо льда, как Земля без кислорода. Талую воду из арктических ледников, доставляют на самолетах и замораживают в идеальные ледяные плиты. Каждый кубик отколотый японским ножом для колки льда не только является произведением искусства сам по себе, но и медленнее таит в коктейле.', 'Ice', 'ing_img/Ice/ice_cubes.jpg', 'Лёд', 1),
(10, 'Лед дробленый', 'Также известный как «бритый» или «битый», разыскивается за порабощение тропических и замороженных коктейлей. Молекулы чистейшей талой воды из арктических ледников замерзают по капле на испарителе и сами просятся в бокал. Остается лишь определиться с чего начать — Мохито, Май Тай или Мятный джулеп. ', 'Ice', 'ing_img/Ice/ice_crushed.jpg', 'Лёд', 1),
(11, 'Сухой лед', 'Твердая двуокись углерода, внешне напоминающая лёд. При комнатной температуре переходит из твердого состояния сразу в парообразное, минуя жидкую фазу. Используется для охлаждения мороженого или создания мистической атмосферы (чтобы появился дым, надо налить на лед немного воды. Лучше горячей). Температура сухого льда около -78,5°, поэтому руками лучше его не трогать. Если к куску сухого льда прижать ложку, то можно услышать писк :)', 'Ice', 'ing_img/Ice/ice_dry.jpg', 'Лёд', 1),
(12, 'Водка', 'Водка сочетается практически с любыми коктейльными ингредиентами. Произведенная путем многократной очистки спирта смешанного с водой, настоящая водка не имеет добавок. Благодаря этому обладает нейтральным запахом и вкусом, что делает ее идеальной основой и для терпкой Кровавой Мэри и сливочного Белого русского. Отличительной особенностью водки ABSOLUT стал креативный подход ко всему: начиная от выращивания зерна для спирта исключительно шведскими семьями и продолжая сотрудничеством с королем поп-арта Энди Уорхоллом. 40%, 500 мл, Швеция.\r\n\r\n', 'strong', 'ing_img/strong/vodka.jpg', 'Крепкий алкоголь', 1),
(13, 'Белый ром', 'Тропический бар в одной бутылке. Изготовленный из патоки сахарного тростника белый ром выдерживается в обожженных бурбоновых бочках не менее 12 месяцев. Обладает легким, не навязчивым и сладким вкусом, чем и полюбился всему миру в Мохито и Дайкири. 37,5%, 700 мл, Тринидад и Тобаго.\r\n', 'strong', 'ing_img/strong/white_rum.jpg', 'Крепкий алкоголь', 1),
(14, 'Джин', 'Английская классика, никогда не выходящая из моды. Перегоняется из пшеничного спирта с добавлением можжевельника, часто в дополнение настаивается на травах и специях. Благодаря сухому консервативному вкусу джин редко употребляют в чистом виде, но когда дело доходит до простого на первый взгляд Джин Тоника или Френч 75 рокировка происходит мгновенно, и нотки цитрусовых меняют всё. Greenall’s — один из самых авторитетных сухих английских джинов разливается на старейшей в Великобритании дистиллерии с 1761 года. ', 'strong', 'ing_img/strong/jin.jpg', 'Крепкий алкоголь', 1),
(15, 'Серебряная текила', 'Огненная любовь к жизни и дух праздника из Мексики. По закону молодая текила содержит не менее 51% сахара из сока голубой агавы и почти всегда разливается сразу после дистилляции. Оттенки гвоздики и сирени в аромате и легкий сладкий вкус агавы неповторимо раскрываются в солнечной Маргарите. 48%, 700 мл, Мексика.', 'strong', 'ing_img/strong/silver_tekila.jpg', 'Крепкий алкоголь', 1),
(16, 'Виски шотландский', 'Законодатель джентельмен клубов — идеально подчеркивает радость от общения с интересным собеседником. Для шотландского виски ячменный солод сушат над торфом, впоследствии полученные из разного солода спирты смешивают, а купаж выдерживают в дубовых бочках не менее 3 лет. Дымный вкус и серьезный характер неразделимы, как Кровь и песок. В купаж Ballantine''s Finest входит более 50 односолодовых и 4 зерновых сортов виски, собранных со всей Шотландии. 40%, 700 мл, Шотландия. \r\n\r\n', 'strong', 'ing_img/strong/skotch_whiskey.jpg', 'Крепкий алкоголь', 1),
(17, 'Апельсиновый ликер', 'Наверное, самый популярный в мире ликер, входящий в состав каждого пятого классического коктейля. Эссенция высушенной на солнце цедры апельсинов и кюрасао, смешивается с водой и нейтральным спиртом для дальнейшей тройной дистилляции. Так, каких-то 150 лет назад, тройная сухость и насыщенность вкуса вдохновили бармена на создание Сайдкара. 40%, 700 мл, Нидерланды.\r\n', 'liqueur', 'ing_img/liqueur/triplesec(orange_liqeur).jpg', 'Ликеры и настойки', 1),
(19, 'Кампари', 'Создан во времена рассвета прекрасной оперной эпохи, как писал Бегбедер, для тех, «чье одиночество лишь продолжение индивидуализма». Оригинальная настойка на 68 ингредиентах, включая цедру горких апельсинов, хинин и женьшень, дистиллируется с водой и натуральным спиртом. Классические рецептуры бренда - Американо или Негрони, всегда украшаются апельсином, по мнению мастеров купажа, лайм и лимон нарушают вкусовое восприятие. 25%, 1000 мл, Италия.', 'liqueur', 'ing_img/liqueur/campari.jpg', 'Ликеры и настойки', 1),
(20, 'Айриш крим', 'Возможно, еще одна секретная разработка ЦРУ, с появлением которой нет больше в мире барной стойки, где не подавали бы Б-52. Сливочный ликёр сочетает в себе ирландский виски и сливки, и уже более четверти века обеспечивает гостям настоящий Взрыв мозга. 17%, 700 мл, Нидерланды. \r\n\r\n', 'liqueur', 'ing_img/liqueur/irish_cream.jpg', 'Ликеры и настойки', 1),
(21, 'Кофейный ликер', 'В то время, когда гениальность зависит лишь от энергии, горячий бленд колумбийского кофе замачивается в ромовом дистилляте и обогащается добавлением ванили и какао. И хотя герои Шварцнейгера и Ван Дама пили его в чистом виде, нам ближе Большой Лебовский и его Белый русский. 24%, 700 мл, Нидерланды.', 'liqueur', 'ing_img/liqueur/cofe-liquer.jpg', 'Ликеры и настойки', 1),
(22, 'Ликер кислая вишня', 'Счастье не стоит искать как золото, его нужно смешивать самостоятельно. Первые заказы на выдержанный 2 года коньячный ликер на основе вишни с разбитыми и отфильтрованными косточками доставляли еще голуби. Они же стали основой для первого в мире летательного аппарата, за 400 лет до нашей эры построенного философом Архитом Таренским. Сегодня взлететь к солнцу стало гораздо проще, стоит лишь заказать Авиацию. 30%, 700 мл, Нидерланды. ', 'liqueur', 'ing_img/liqueur/red_cherry.jpg', 'Ликеры и настойки', 1),
(23, 'Сухой вермут', 'Сухой Мартини как эликсир спокойствия использовали Уинстон Черчилль, предпочитавший налить в бокал охлажденный джин, а бутылку вермута поставить рядом для красоты, Хичкок, бросавший лишь «5-секундный взгляд на вермут». Для сухого вермута белое крепленое вино настаивают на альпийской полыни с добавлением десятка других ингредиентов, включая ромашку и лепестки роз. 18%, Италия.', 'vermouth', 'ing_img/vermouth/dry_vermoth.jpg', 'Вермуты', 1),
(24, 'Красный вермут', 'По стечению обстоятельств Манхэттен - один из самых первых коктейлей, появившихся на основе красного вермута, - определил всю дальнейшую судьбу итальянского напитка. Своим рождением знаменитый вермут обязан союзу вина, спирта, сахара и 35 альпийским травам и пряностям. 15%, Италия.', 'vermouth', 'ing_img/vermouth/red_vermoth.jpg', 'Вермуты', 1),
(25, 'Белый вермут', 'Креплёное вино, ароматизированное пряными и лекарственными травами, цветами, семенами и фруктами, среди которых: полынь, мята, корица, мускатный орех, кора хинного дерева. Крепость 15%.', 'vermouth', 'ing_img/vermouth/white_vermoth.jpg', 'Вермуты', 1),
(26, 'Розовый вермут', 'Креплёное вино, ароматизированное пряными и лекарственными травами, цветами, семенами и фруктами, среди которых: полынь, мята, корица, мускатный орех, кора хинного дерева. Розовый — единственный вермут, в производстве которого используется розовое вино. Крепость 15%.', 'vermouth', 'ing_img/vermouth/rose_vermoth.jpg', 'Вермуты', 1),
(27, 'Пиво светлое', 'Древний алкогольный напиток, получаемый в результате брожения солодового сусла с добавлением хмеля. Крепость около 6%.', 'beer_cidre', 'ing_img/beer_cidre/beer_light.jpg', 'Пиво и сидр', 1),
(28, 'Пиво темное', 'Слабоалкогольный напиток, получаемый спиртовым брожением солодового сусла с добавлением пивных дрожжей и хмеля. Темное пиво производится из темного или карамельного солода. Крепость 3-6%.', 'beer_cidre', 'ing_img/beer_cidre/beer_dark.jpg', 'Пиво и сидр', 1),
(29, 'Яблочный сидр', 'Шампанизированный слабоалкогольный напиток, получаемый путем сбраживания яблочного сока без добавления дрожжей. Обладает золотистым или зеленоватым цветом и яблочным ароматом. Крепость 6%.', 'beer_cidre', 'ing_img/beer_cidre/apple_cidre.jpg', 'Пиво и сидр', 1),
(30, 'Красное вино сухое', 'Алкогольный напиток, получаемый спиртовым брожением виноградного сока. Цвет может варьироваться от светло-рубинового до темно-гранатового. Для приготовления коктейлей чаще используют сухие вина, вкус которых лучше сочетается с другими ингредиентами. 13,5%, 750 мл, США.', 'wine', 'ing_img/wine/red_dry_vine.jpg', 'Вина', 1),
(31, 'Белое вино сухое', 'Алкогольный напиток, получаемый спиртовым брожением виноградного сока. Цвет от светло-соломенного до янтарного. Для приготовления коктейлей чаще используют сухие вина, вкус которых лучше сочетается с другими ингредиентами. 13,5%, 750 мл, США.', 'wine', 'ing_img/wine/white_dry_vine.jpg', 'Вина', 1),
(32, 'Херес Педро Хименес', 'Херес — это крепкое испанское вино. Педро Хименес — самый сладкий вид хереса. Обладает темным цветом, густой консистенцией и сильным ароматом. 15%, 740 мл, Испания.', 'wine', 'ing_img/wine/heres_pedro_himenes.jpg', 'Вина', 1),
(33, 'Крепленое вино красное', 'Алкогольный напиток, получаемый путем сбраживания виноградного сусла с добавлением спирта. Молодой и полный энергии портвейн недолго выдерживают в дубовых бочках 3-4 года, что придает ему яркий вкус красных ягод, насыщенный рубиновый цвет и фруктовый аромат. 20%, 750 мл, Португалия.\r\n\r\n', 'wine', 'ing_img/wine/strong_red_vine.jpg', 'Вина', 1),
(34, 'Саке', 'Традиционный японский алкогольный напиток, получаемый путем сбраживания риса. Цвет желтоватый. Во вкусе могут ощущаться фруктовые нотки. Крепость 15%, 720 мл, Япония.', 'wine', 'ing_img/wine/sake.jpg', 'Вина', 1),
(35, 'Ангостура биттер', 'Легкая горечь ароматического биттера Ангостура усиливает вкусовые качества коктейля и придает особенное приятное послевкусие. Рецептура биттера насчитывает почти 200 лет и хранится в секрете. Крепость 44,7%.\r\n', 'bitter', 'ing_img/bitter/angostura_bitter.jpg', 'Биттеры', 1),
(36, 'Апельсиновый биттер', 'Апельсиновый ароматический биттер — необходимый элемент для приготовления множества коктейлей. Рецептура ароматических трав и специй, входящих в состав биттеров Ангостура держится в тайне и известна только производителю. Крепость 28%.\r\n', 'bitter', 'ing_img/bitter/orange_bitter.jpg', 'Биттеры', 1),
(37, 'Пишо биттер', 'Второй грааль коктейльной культуры, без которого сложно повесить перед своим заведением вывеску «бар». Как и Ангостура, появился в 30-х годах 19 века — в секретной формуле яркой горечи точно прослеживаются анис, гвоздика и ваниль. Своей международной барной экспансией обязан коктейлю и компании Сазерак. 35%, 100 мл, США (Кентуки).', 'bitter', 'ing_img/bitter/pisho_bitter.jpg', 'Биттеры', 1),
(38, 'Грейпфрутовый биттер', 'Концентрированная ароматическая добавка, изготавливаемая из грейпфрутов.', 'bitter', 'ing_img/bitter/grapefruit_bitter.jpg', 'Биттеры', 1),
(39, 'Персиковый биттер', 'Концентрированная ароматическая добавка, изготавливаемая из персиков.', 'bitter', 'ing_img/bitter/peach_bitter.jpg', 'Биттеры', 1),
(40, 'Гвоздичный биттер', 'Концентрированная ароматическая добавка, изготовленная из сухих почек тропического гвоздичного дерева.', 'bitter', 'ing_img/bitter/gvozdika_bitter.jpg', 'Биттеры', 1),
(41, 'Ревеневый биттер', 'Концентрированная ароматическая добавка с деликатным запахом ревеня. Известна с 1800-х годов.', 'bitter', 'ing_img/bitter/reven_bitter.jpg', 'Биттеры', 1),
(42, 'Биттер грецкий орех', 'Концентрированная ароматическая добавка, изготовленная из грецкого ореха. Придаёт коктейлю характерную терпкость грецкого ореха и добавляет во вкус нотки шоколада. ', 'bitter', 'ing_img/bitter/black_walnut_bitter.jpg', 'Биттеры', 1),
(43, 'Лимонный биттер', 'Концентрированная ароматическая добавка, изготавливаемая из лимонов. Всего 1-2 капли придуют коктейлю аромат лимона и лимонной травы.', 'bitter', 'ing_img/bitter/lemon_bitter.jpg', 'Биттеры', 1),
(44, 'Сельдереевый биттер', 'Впервые был произведен в XIX веке. Ароматическая добавка со вкусом сельдерея с добавлением цитрусовых и пряных ноток.', 'bitter', 'ing_img/bitter/celery_bitter.jpg', 'Биттеры', 1),
(45, 'Сливовый биттер', 'Концентрированная ароматическая добавка, изготавливаемая из сливы.', 'bitter', 'ing_img/bitter/plum_bitter.jpg', 'Биттеры', 1),
(46, 'Клюквенный биттер', 'Концентрированная ароматическая добавка, изготавливаемая из клюквы.', 'bitter', 'ing_img/bitter/cranberry_bitter.jpg', 'Биттеры', 1),
(47, 'Шоколадный биттер', 'Концентрированная ароматическая добавка с незабываемым вкусом шоколада.', 'bitter', 'ing_img/bitter/chocolate_bitter.jpg', 'Биттеры', 1),
(48, 'Мятный биттер', 'Концентрированная ароматическая добавка, изготавливаемая из мяты.', 'bitter', 'ing_img/bitter/mint_bitter.jpg', 'Биттеры', 1),
(49, 'Сахарный сироп', 'Гармония создания требует любви, как хороший коктейль баланса кислого и сладкого. Сегодня каждый бармен готовит сахарный сироп в своей идеальной пропорции, впрочем большинство сходится на соотношении 2 частей сахара к 1 воды. Остается лишь прислушаться к Елене Олсен — «Почему дробленый лед придает-дарит, Мохито и Май Тай столько хорошего? Возможно, потому, что внутри есть ром?». ', 'syrup', 'ing_img/syrup/sugar_syrup.jpg', 'Сиропы', 1),
(50, 'Медовый сироп', 'Monin производит отличные сиропы во Франции с 1912 г. Богат изобилием ароматов, фруктов и цветовых оттенков. Бармены 32 стран признали Monin лучшей маркой сиропов.', 'syrup', 'ing_img/syrup/honey_syrup.jpg', 'Сиропы', 1),
(51, 'Гренадин', 'Сладкая способность видеть чудесное в обыкновенном — истинный источник цвета в розовых коктейлях и очках. Первая рецептура включала в себя гранатовый сок и сахарный сироп, сегодня наряду с гранатом в состав сиропа входят другие красные ягоды и ваниль. Время, как и Сингапурский слинг, уносит любую грусть стоит лишь «добавить полторы части сладкого. Плохого не посоветую — я знаю о чем говорю!», — The New York Times 1908 год. 1000 мл, Франция.', 'syrup', 'ing_img/syrup/grenadine.jpg', 'Сиропы', 1),
(52, 'Ванильный сироп', 'Monin Vanille\nMonin производит отличные сиропы во Франции с 1912 г. Богат изобилием ароматов, фруктов и цветовых оттенков. Бармены 32 стран признали Monin лучшей маркой сиропов.', 'syrup', 'ing_img/syrup/vanilla_syrup.jpg', 'Сиропы', 1),
(53, 'Сироп маракуйя', 'Monin Fruit de la Passion\r\nMonin производит отличные сиропы во Франции с 1912 г. Богат изобилием ароматов, фруктов и цветовых оттенков. Бармены 32 стран признали Monin лучшей маркой сиропов.', 'syrup', 'ing_img/syrup/marakuya_syrup.jpg', 'Сиропы', 1),
(54, 'Апельсиновый сок', 'Коктейль из апельсинового сока и игристого вина, символ не прекращающегося даже утром праздника, Мимоза был подарен миру в 1925 году парижским отелем Ритц. Из одного апельсина можно выжать немногим больше 100 мл сока. Апельсиновый сок Rich делается из лучших сортов апельсинов, созревших под ласковым солнцем. 1 л', 'juice', 'ing_img/juice/orange_juice.jpg', 'Соки и морсы', 1),
(55, 'Яблочный сок', 'Яблочный сок Rich - это лучшие сорта яблок, которые дарят освежающее море удовольствия. Прочувствовать элегантный баланс между сладостью и нежной кислинкой лучше всего можно в Пьяном золоте или Яблочно-имбирной маргарите.', 'juice', 'ing_img/juice/apple_juice.jpg', 'Соки и морсы', 1),
(56, 'Клюквенный морс', 'Клюквенный морс занимает 7 место в рейтинге самых полезных в мире напитков. Витамины, содержащиеся в нем, защищают организм от микробов, таким образом клюквенный морс способен значительно повысить иммунитет. ', 'juice', 'ing_img/juice/cranberry_mors.jpg', 'Соки и морсы', 1),
(57, 'Ананасовый сок', 'В 1954 году Ромон Марреро, бармен Карибского отеля «Хилтон», сумел воссоздать считавшийся утерянным рецепт знаменитого пирата Роберто Конфреси. Коктейль из рома, ананаса и кокоса был назван Пинья Колада, или «процеженный ананас». Из одного ананаса в среднем получается 500 мл сока. Ананасовый сок Rich делается из лучших плодов, созревших под жарким тропическим солнцем. Богатый и насыщенный вкус достигается благодаря смешению разных сортов ананасов. 1 л', 'juice', 'ing_img/juice/ananas_juice.jpg', 'Соки и морсы', 1),
(58, 'Вода без газа', 'Вода, как и лед, необходима в баре как воздух. Талая ледниковая вода согласно правилам хорошего тона разливается только в стекло. Глоток чистой воды между каждым коктейлем — знак хорошего барного образования. 275 мл, Германия. ', 'water_drink', 'ing_img/water_drink/water_without_gas.jpg', 'Вода и напитки', 1),
(59, 'Содовая', 'Свойства содовой придавать напиткам более праздничное игристое настроение были по достоинству оценены еще Чарльзом Дикенсом, предпочитавшим авторскую версию Тома Коллинза с джином. Своей доступностью и названием содовая обязана Якобу Швепу, впервые добавившему в XIX веке в воду пищевую соду вместо газации углекислым газом. 275 мл, Америка', 'water_drink', 'ing_img/water_drink/soda.jpg', 'Вода и напитки', 1),
(60, 'Спрайт', 'Безалкогольный газированный напиток с лимонным вкусом. Тонизирует и освежает. Основными поставщиками спрайта являются компании Coca-Cola и Pepsi.', 'water_drink', 'ing_img/water_drink/sprite.jpg', 'Вода и напитки', 1),
(61, 'Имбирный эль', 'Младший брат имбирного пива, и, как заведено в семье, идеально миксуется с ромом или бурбоном. Сильногазированная вода ароматизируется имбирем с добавлением кукурузной фруктозы и карамели для цвета. Когда пьешь обманчиво сладкую и легкую Лошадиную шею, не стоит забывать, что если поставить перед лошадь ведро с кофе и с какао - в 4-х случаях из 5 лошадь выберет кофе. Так и человеку после каждого 5 коктейля не помешает эспрессо. 250 мл.', 'water_drink', 'ing_img/water_drink/imbir_el.jpg', 'Вода и напитки', 1),
(62, 'Кола', 'Беспроигрышный эликсир удовольствия для коктейльных скептиков. Рецептура самого популярного газированного напитка Америки хранится в секрете, впрочем, наличие кофеина, сахара, бесчисленного числа ароматизаторов и красителей ни для кого не секрет. С легкостью прячет крепость и вкус любого алкоголя даже в Лонг Айленде, что уж говорить о других коктейлях...', 'water_drink', 'ing_img/water_drink/cola.jpg', 'Вода и напитки', 1),
(63, 'Грейпфрутовый сок', 'Только лучшие грейпфруты становятся соком Rich. Благодаря сочетания разных сортов достигается удивительный вкус. Легкая горчинка вместе с удивительной сладостью грейпфрутового сока Rich незаменимы в Соленой собаке или Хемингуэй дайкири. \r\n\r\n', 'juice', 'ing_img/juice/grapefruit_juice.jpg', 'Соки и морсы', 1),
(68, 'Коньяк', '    Стиль и роскошь французских дворов — подарок из прошлого монархам собственной жизни. Приоритетно белый виноград сорта Уни Блан отжимается без дробления косточек, проходит двух-этапную ферментацию и выдерживается в дубовых бочках ручной работы не менее 2 лет. К счастью срок выдержки не ограничен, и с годами в аромате и вкусе уже слышится Франция — цветы, фрукты и специи идеально подчеркивающие персиковый вкус Джу Джулепа и апельсиновый мир Сайдкара. 40%, 500 мл, Франция (Коньяк). ', 'strong', 'ing_img/strong/konjak.jpg', 'Крепкий алкоголь', 1),
(69, 'Бурбон', '    Статуя свободы современного бара. Производится из зерна, с содержанием не менее 51 % кукурузы и, как говорят в Кентуки, выдерживается не менее 2 лет в дубовых бочках вида кожи аллигатора, обоженных изнутри по специальной технологии. Неповторимый карамельный вкус с легкой ухмылкой Черчиля придает Манхэттену цветочный аромат с древесными нотками. 40%, 700 мл, США.', 'strong', 'ing_img/strong/burbon.jpg', 'Крепкий алкоголь', 1),
(70, 'Темный ром', 'Темная лошадка коктейльного бара. Зрелый брат белого рома также готовится путем брожения быстрых дрожей и патоки из сахарного тростника, но выдерживается в обугленных дубовых бочках от бурбона. Прикрываясь нотками карамели, всегда приходит первым на финише, не важно ставил ты на Ураган или чистую подачу. 40%, 700 мл, Тринидад и Тобаго.', 'strong', 'ing_img/strong/dark_rum.jpg', 'Крепкий алкоголь', 1),
(71, 'Золотая текила', 'Звезда любой мексиканской вечеринки, завораживающая своим ярким цветом и необычным вкусом. По сути своей это серебряная текила, подкрашенная карамелью и сдобренная ароматическими добавками. Благодаря сладковато-цитрусовым оттенкам во вкусе она будто подмигивает тебе из бокала Флирт маргариты. Olmeca Gold — это купаж Blanco и выдержанных Anejo. 38%, 700 мл, Мексика.', 'strong', 'ing_img/strong/gold_tekila.jpg', 'Крепкий алкоголь', 1),
(72, 'Золотой ром', 'Золото тики-культуры и билет на «Завтрак у Тиффани». Карамелизированный ром из сока сахарного тростника выдерживается в дубовых бочках. Как и Холли Голайтли просит лишь об одном — будь то Май Тай или Зомби — «Обещайте мне одну вещь: не отвозите меня домой, пока я не напьюсь». 40%, 700 мл, Тринидад и Тобаго. ', 'strong', 'ing_img/strong/gold_rum.jpg', 'Крепкий алкоголь', 1),
(73, 'Абсент', 'Муза, томимая жаждой», страсть Пабло Пикассо, вдохновение Хемингуэйя и Ремарка. Основной компонент - горький экстракт полыни, содержащий туйон, бесцветное вещество с характерным запахом, напоминающим ментол. Терпкий вкус и аромат абсента нельза спутать, серьезный напиток чувствуется даже после омовения рокса в Сазераке. 70%, 700 мл, Франция.', 'strong', 'ing_img/strong/absent.jpg', 'Крепкий алкоголь', 1),
(74, 'Водка ванильная', 'Романтическая история с продолжением в твоем баре. В дополнение к классической рецептуре чистейший спирт ароматизируется экстрактами ванили. Карамельные нотки и слабый привкус горького шоколада в Энидей энивей шоте перенесут на берега океана поближе к Карибскому бризу. Отличительной особенностью водки ABSOLUT стал креативный подход ко всему: начиная от выращивания зерна для спирта исключительно шведскими семьями и продолжая сотрудничеством с королем поп-арта Энди Уорхоллом. 40%, 700 мл, Швеция. ', 'strong', 'ing_img/strong/vodka_vanila.jpg', 'Крепкий алкоголь', 1),
(75, 'Кальвадос', '    Источник безграничной книжной романтики и неиссякаемого аппетита во время званных ужинов. Получается путем перегонки сидра из разных сортов яблок, выдержанного в дубовых бочках не менее 2 лет. Как говорила героиня Ремарка, — «кальвадос скорее вдыхаешь, чем пьешь и, попробовав один раз, ты мечтаешь об этом». Со своей стороны добавим главное — первое трепетное знакомство, для которого рекомендуем Джек Роуз. 40%, 700 мл, Франция.', 'strong', 'ing_img/strong/kalvados.jpg', 'Крепкий алкоголь', 1),
(76, 'Водка цитрон', 'Возможно, появилась на свет только ради одного всемогущего коктейля. В дополнение к классической рецептуре чистейший спирт ароматизируется экстрактами мандарина, апельсина, лимона, лайма и грейпфрута. Ароматы цитрусовых и горящие масла цедры — вот как получился Космополитен, сразивший Мадонну. Отличительной особенностью водки ABSOLUT стал креативный подход ко всему: начиная от выращивания зерна для спирта исключительно шведскими семьями и продолжая сотрудничеством с королем поп-арта Энди Уорхоллом. 40%, 500 мл, Швеция.', 'strong', 'ing_img/strong/vodka_citron.jpg', 'Крепкий алкоголь', 1),
(77, 'Виски ирландский', 'Вечно молодой и харизматичный бунтарь из Ирландии. Ячменный солод для него сушат в печах, спирт проходит тройную перегонку и только потом отправляется в среднем на 5-6 лет в дубовые бочки. Вкус столь же мягкий, как сливки в горячем и душевном Ирландском кофе. Среди поклонников ирландского виски Jameson Лэди Гага, Рианна и Пинк. 40%, 700 мл, Ирландия.', 'strong', 'ing_img/strong/irish_whiskey.jpg', 'Крепкий алкоголь', 1),
(78, 'Водка малиновая', 'Малиновое удовольствие в бутылке. В дополнение к классической рецептуре чистейший спирт ароматизируется экстрактами спелой и свежей малины. Благодаря насыщенному фруктовому вкусу так просто устроить себе Бабье лето даже в декабре. Отличительной особенностью водки ABSOLUT стал креативный подход ко всему: начиная от выращивания зерна для спирта исключительно шведскими семьями и продолжая сотрудничеством с королем поп-арта Энди Уорхоллом. 40%, 700 мл, Швеция.', 'strong', 'ing_img/strong/vodka_raspberry.jpg', 'Крепкий алкоголь', 1),
(79, 'Водка мандариновая', 'Вкус новогоднего настроения в бутылке. В дополнение к классической рецептуре чистейший спирт ароматизируется экстрактами спелых мандаринов и цедрой апельсинов. Доверься чарующему вкусу, и Секс с Анфисой точно будет. Отличительной особенностью водки ABSOLUT стал креативный подход ко всему: начиная от выращивания зерна для спирта исключительно шведскими семьями и продолжая сотрудничеством с королем поп-арта Энди Уорхоллом. 40%, 700 мл, Швеция.', 'strong', 'ing_img/strong/vodka_mandarin.jpg', 'Крепкий алкоголь', 1),
(80, 'Водка перечная', 'Пикантный микс из чили, халапеньо и зеленого колокольчика в бутылке. В дополнение к классической рецептуре чистейший спирт ароматизируется экстрактами перца. Поговаривают, что сам Адвокат дьявола продал душу за этот чарующий вкус. Отличительной особенностью водки ABSOLUT стал креативный подход ко всему: начиная от выращивания зерна для спирта исключительно шведскими семьями и продолжая сотрудничеством с королем поп-арта Энди Уорхоллом. 40%, 500 мл, Швеция.', 'strong', 'ing_img/strong/vodka_peppar.jpg', 'Крепкий алкоголь', 1),
(81, 'Водка черносмо-родиновая', 'Вкус черносмородинового сада в бутылке. В дополнение к классической рецептуре чистейший спирт ароматизируется экстрактами спелой черной смородиной. Необыкновенный вкус с легкой кислинкой станет отличным дополнением Летнему вечеру в Гаграх и поможет покорить сердце самой Наоми. Отличительной особенностью водки ABSOLUT стал креативный подход ко всему: начиная от выращивания зерна для спирта исключительно шведскими семьями и продолжая сотрудничеством с королем поп-арта Энди Уорхоллом. 40%, 500 мл, Швеция.', 'strong', 'ing_img/strong/vodka_kurant.jpg', 'Крепкий алкоголь', 1),
(82, 'Водка грушевая', 'Буйство вкуса и аромата грушевого сада в бутылке. В дополнение к классической рецептуре чистейший спирт ароматизируется экстрактами нежных и спелых груш. Пальма де груша и Пирсберри подарят минутку летнего солнца даже в самый непогожий день. Отличительной особенностью водки ABSOLUT стал креативный подход ко всему: начиная от выращивания зерна для спирта исключительно шведскими семьями и продолжая сотрудничеством с королем поп-арта Энди Уорхоллом. 40%, 700 мл, Швеция.', 'strong', 'ing_img/strong/vodka_pears.jpg', 'Крепкий алкоголь', 1),
(83, 'Кашаса', '    Национальный напиток Бразилии с присущей легкостью переносит карнавал из Рио Де Жанейро прямо за стойку бара. Дистиллируют из забродившего сока сахарного тростника по единой схеме — «голову» и «хвост» отбрасывают, оставляя лишь «сердце» напитка для дальнейшей выдержки в коньячных бочках. Популярность напитку принес его схожий с ромом вкус и аромат, так восхитительно раскрывшийся в тропическом коктейле Кайпиринья. 40%, 700 мл, Бразилия.', 'strong', 'ing_img/strong/kashasa.jpg', 'Крепкий алкоголь', 1),
(84, 'Пряный ром', 'Любимый напиток пиратов, корсаров, флибустьеров и кладоискателей - одним словом, всех настоящих любителей приключений. Пряный ром выдерживается в дубовых бочках от 1 до 3 лет с корицей, мускатом, медом, ванилью и клёном. Благодаря Эффекту бабочки пить его хочется снова и снова. 35%, 700 мл, Великобритания.', 'strong', 'ing_img/strong/rum_spicy.jpg', 'Крепкий алкоголь', 1),
(85, 'Ром-151', '    Настоящее пламя страсти в твоем баре. Очень крепкий ром, настолько крепкий, что легко воспламеняется и в чистом виде почти не употребляется. Он выдерживается не менее 1-го года в небольших бочонках из американского белого дуба, обугленных изнутри. Устрой вечеринку с Раулем и Фиделем и, возможно, после пары коктейлей мечта посмотреть на Горящий Феррари может стать явью. 75%, 750 мл, Пуэрто Рико. ', 'strong', 'ing_img/strong/rum_151.jpg', 'Крепкий алкоголь', 1),
(86, 'Шоколадная текила', '    Два легендарных подарка древних племен Мексики - текила и шоколад, в одной бутылке. Оттенки какао, жареного фундука, ванили, травяные нотки агавы аромате и нежный сладкий вкус темного шоколада неповторимо раскрываются в мистическом Дьябло или освежающей Шоколадно-мятной Маргарите. 35%, 700 мл, Мексика.', 'strong', 'ing_img/strong/cholotate_tekila.jpg', 'Крепкий алкоголь', 1),
(87, 'Писко', '    «Летящая птица», пережившая конкистадоров и тиранов, ради того, чтобы подарить гостям истинное чувство свободы. Писко относят к категории виноградного бренди, отсюда и схожесть в принципах производства — свежесброженное вино из мускатного винограда без кожицы и косточек дистиллируют и выдерживает в дубовых бочках не менее 2 месяцев. Во время споров между Чили и Перу за право называть писко своим напитком, Чили в 2007 году объявил Писко Сауэр национальным достоянием страны. 43%, 700 мл, Перу.', 'strong', 'ing_img/strong/pisko.jpg', 'Крепкий алкоголь', 1),
(88, 'Выдержанный ром', '    Страсть кубинских красоток и лучи яркого солнца в твоем баре. Изготавливается из нескольких видов рома, которые выдерживаются в боках из белого дуба не менее 7 лет. Вкусовые нотки ванили, кедра, каштана и специй покорили сердца самых привередливых красоток в Барбара ти панч. 40%, 700 мл, Тринидад и Тобаго. ', 'strong', 'ing_img/strong/rum_old.jpg', 'Крепкий алкоголь', 1),
(89, 'Виски выдержанный 12 лет', 'В выдержанный виски входит более 40 отборных сортов зерновых и солодовых виски, минимальный срок выдержки которых составляет 12 лет. Напиток отличает насыщенный золотистый цвет и сливочный сбалансированный вкус с нотками цветов, меда и дуба. 40%, 700 мл, Шотландия. ', 'strong', 'ing_img/strong/whiskey_12.jpg', 'Крепкий алкоголь', 1);
INSERT INTO `ingridients` (`id`, `ing_title`, `ing_desc`, `ing_type`, `ing_img`, `ing_type_rus`, `is_ing`) VALUES
(90, 'Виски выдержанный 21 год', 'Настоящий король любой барной коллекции. Виски Chivas Royal Salute появился в 1953 году по случаю коронации королевы Елизаветы II. Настоящий шедевр, в купаж которого входят редчайшие сорта виски в возрасте от 21 года, отличается изысканным ароматом и утонченным мягким вкусом с нотками розовых лепестков и карамели. На выбор ценителей представлены три коллекционных графина из фарфора красного, зеленого и синего цветов, названных в честь рубина, изумруда и сапфира.40%, 700 мл, Шотландия. ', 'strong', 'ing_img/strong/whiskey_21.jpg', 'Крепкий алкоголь', 1),
(91, 'Виски ирландский резерв', 'Редкий виски, при производстве которого мастера купажа смешивают солодовый ирландский виски тройной перегонки и небольшую часть редкого зернового виски, который выдерживался в обожжённых бочках от бурбона и хереса. В результате - получается виски с удивительно богатым, роскошным вкусом, в котором сочетаются пряности и экзотические фрукты. 40%, 700 мл, Ирландия.', 'strong', 'ing_img/strong/irish_whiskey_select_reserve.jpg', 'Крепкий алкоголь', 1),
(92, 'Выдержанный ром 12 лет', '    Смесь лучших зрелых сортов рома в одной бутылке. Такие ромы выдерживается в обугленных дубовых бочках из-под американского бурбона, после чего мастер купажа смешивает лучшие из получившихся и снова отправляет в бочку. После того, как ром достигает своей максимальной зрелости, его фильтруют и бутилируют. Удовольствие в ограниченном количестве уже давно по праву заняло почетное место в домашних барах истинных ценителей. 40%, 700 мл, Тринидад и Тобаго. ', 'strong', 'ing_img/strong/rum_12.jpg', 'Крепкий алкоголь', 1),
(93, 'Односолодовый виски', 'Классический джентельмен родом из солнечного уголка Шотландии - Спейсайда в долине реки Ливет. Односолодовый виски выдерживается в бочках из белого американского дуба не менее 12 лет. В результате получается напиток янтарного цвета и утонченного вкуса с нотками вереска, фруктов, ванильной карамели. Терпкий аромат торфяных костров сопровождает последние ноты пряного имбиря, лесного ореха и марципана. Продолжает завораживать и красть сердца ценителей по всему миру. 40%, 700 мл, Шотландия. ', 'strong', 'ing_img/strong/malt_whiskey.jpg', 'Крепкий алкоголь', 1),
(94, 'Односолодовый виски 18 лет', 'Классический джентельмен родом из солнечного уголка Шотландии - Спейсайда в долине реки Ливет. Односолодовый виски выдерживается в бочках из-под бурбона не менее 18 лет. В результате получается напиток с ароматом дуба, ириса, мяты и шоколада. Во вкусе улавливается свежесть апельсина со специями и приятная горечь дуба, послевкусие играет торфяными нотками. Продолжает завораживать и красть сердца ценителей по всему миру, и давно занял почетное место в домашних барах коллекционеров. 43%, 700 мл, Шотландия. ', 'strong', 'ing_img/strong/malt_whiskey_18.jpg', 'Крепкий алкоголь', 1),
(95, 'Портвейн Тони', '    Самая популярная категория крепленых вин, как и бабушкино фруктовое варенье — стоит только открыть бутылку, и остановиться уже невозможно. Два-три дня брожения красного винограда прерывается добавлением виноградного спирта крепостью в 77%, затем закрепленное вино выдерживается не менее 2 лет в дубовых бочках методом Солера. Легкий контакт с деревом дарит яркому фруктовому аромату ореховые нотки во вкусе: и вот перед нами идеальная замена кофе — Кофе коктейль. 19,5%, 750 мл, Португалия. ', 'wine', 'ing_img/wine/portvein_tony.jpg', 'Вина', 1),
(96, 'Херес Олоросо', 'Разновидность крепленого вина. «Олоросо» в переводе с испанского — душистый. В зависимости от технологии приготовления, может быть сухим, полусухим и сладким. Крепость 20%.', 'wine', 'ing_img/wine/heres_olorosso.jpg', 'Вина', 1),
(97, 'Херес Фино', 'Херес — это крепкое испанское вино. Фино — сухой вид хереса. 15%, 750 мл, Испания.', 'wine', 'ing_img/wine/heres_fino.jpg', 'Вина', 1),
(98, 'Сливовое вино', '    Традиционный японский напиток, рецепт которого известен около 400 лет. Изготавливается путем брожения измельченной мякоти сливы. Сливовое вино созревает около полугода. Обладает терпким и пряным ароматом. 12,5%, 750 мл, Япония. ', 'wine', 'ing_img/wine/plum_vine.jpg', 'Вина', 1),
(99, 'Херес Амонтильядо', 'Херес — это крепкое испанское вино. Амонтильядо — полусладкий херес янтарного цвета. Он темнее, чем херес фино, но светлее олоросо. 17%, 750 мл, Испания.', 'wine', 'ing_img/wine/heres_amontialado.jpg', 'Вина', 1),
(100, 'Выдержанный портвейн 10 лет', '    Тони - самая популярная категория крепленых вин, как и бабушкино фруктовое варенье — стоит только открыть бутылку, и остановиться уже невозможно. Выдержанный портвейн получается в результате смешивания тщательно отобранных портвейнов, выдержанных в дубовых бочках в течение 10 лет. Темно-желтый цвет, богатый аромат с нотками шоколада и дерева дополняют насыщенный бархатистый вкус сухих фруктов. 20%, 750 мл, Португалия. ', 'wine', 'ing_img/wine/portvein_10.jpg', 'Вина', 1),
(101, 'Выдержанный портвейн 20 лет', '    Тони - самая популярная категория крепленых вин, как и бабушкино фруктовое варенье — стоит только открыть бутылку, и остановиться уже невозможно. 20-летний выдержанный портвейн получают путем смешения лучших портвейнов разного возраста, выдержанных в дубовых бочках, при этом на этикетке указывается средний возраст. Сбалансированный сложный вкус с нотами меда и специй сдобрен древесным ароматом, что делает напиток желанным в домашних барах почитателей портвейна по всему миру. 20%, 750 мл, Португалия. ', 'wine', 'ing_img/wine/portvein_20.jpg', 'Вина', 1),
(102, 'Выдержанный портвейн 30 лет', '    Тони - самая популярная категория крепленых вин, как и бабушкино фруктовое варенье — стоит только открыть бутылку, и остановиться уже невозможно. 30-летний выдержанный портвейн получают путем смешения лучших портвейнов средний возраст которых составляет 30 лет, как и указано на этикетке. Насыщенный вкус, богатый нотами специй, дерева и сухих фруктов, достигается благодаря долгосрочной выдержке в дубовых бочках. Бархатистое послевкусие стало гарантом почетного места в домашних барах ценителей портвейна. 20%, 750 мл, Португалия. ', 'wine', 'ing_img/wine/portvein_30.jpg', 'Вина', 1),
(103, 'Крепленое вино белое', '    Алкогольный напиток, получаемый путем сбраживания виноградного сусла с добавлением спирта. Портвейн выдерживается в дубовых бочках и винных чанах и обладает желто-соломенным цветом. Элегантный вкус с нотками тропических фруктов и цветочный аромат переносит в самое сердце солнечной Португалии в любое время года. 20%, 750 мл, Португалия.', 'wine', 'ing_img/wine/strong_white_vine.jpg', 'Вина', 1),
(104, 'Крепленое вино белое сухое', '    Алкогольный напиток, получаемый путем сбраживания виноградного сусла с добавлением спирта. Портвейн выдерживается в дубовых бочках и винных чанах. Отличается соломенно-зеленым цветом, цветочным ароматом и нежным, сухим, сбалансированным вкусом. 20%, 750 мл, Португалия.', 'wine', 'ing_img/wine/strong_white_dry_vine.jpg', 'Вина', 1),
(105, 'Портвейн винтажный', 'Винтажный портвейн позднего розлива производят из сортов винограда одного года урожая и выдерживают в дубовых бочках и винных чанах от 4 до 6 лет. Насыщенным рубиновым цветом, богатым ароматом и изысканным вкусом ягод можно наслаждаться сразу после покупки бутылки, потому что напиток не требует дополнительной выдержки и сделает даже самый холодный вечер солнечнее. 20%, 750 мл, Португалия. ', 'wine', 'ing_img/wine/portvein_old.jpg', 'Вина', 1),
(106, 'Портвейн специальный резерв', '    Тони - самая популярная категория крепленых вин. Как и бабушкино фруктовое варенье - стоит только открыть бутылку, и остановиться уже невозможно. Портвейн специальный резерв выдерживают в дубовых бочках в течение 7 лет. Отличается насыщенным коричнево-бурым цветом, незабываемым ароматом с нотками сухофруктов и специй и бархатистым вкусом, который помогает на время позабыть о повседневной суете. 19,5%, 750 мл, Португалия. ', 'wine', 'ing_img/wine/portvein_special_reserve.jpg', 'Вина', 1),
(107, 'Амаретто ликер', '    Пост-советская ностальгия и вкус знакомый с юности для рожденных в 70-80-е годы. Ликер Амаретто первым появился в России после падения железного занавеса и стал невероятно популярен. Во всех барах мира используется как ингредиент для коктейлей, а также в паре с кофе. Для его основы используются орехи, ваниль и цитрусовые дистилляты, которые гармонично сочетаются друг с другом. Ностальгируем вместе с Бубль Гум и Милки вей. 30%, 700 мл, Нидерланды.', 'liqueur', 'ing_img/liqueur/amaretto.jpg', 'Ликеры и настойки', 1),
(108, 'Абрикосовый ликер', '    Когда грусть-печаль обуревает в хмурую погоду, подарит тепло летнего солнца, радостное настроение и вкус спелых абрикосов. Классический, свежий и мягкий ликер получают из смеси абрикосовых дистиллятов и бренди. И даже, если на вас с друзьями вдруг нападут Зомби, на помощь всегда прийдет Леди киллер. 24%, 700 мл, Нидерланды.', 'liqueur', 'ing_img/liqueur/apricot_liquer.jpg', 'Ликеры и настойки', 1),
(109, 'Дынный ликер', '    Настоящий изумруд в твоем баре. Также как и драгоценный камень, благодаря яркому цвету вызывает блеск в глазах девушек, а необыкновенный вкус дыни вызывает эйфорию. Рецепт ликера пришел к нам из США, зато своей популярностью он обязан коктейлю Зеленая фея, который придумал российский бармен. 24%, 700 мл, Нидерланды.', 'liqueur', 'ing_img/liqueur/melon_liquer.jpg', 'Ликеры и настойки', 1),
(110, 'Мятный ликер зеленый', 'Перефразируя Буковски — «не храните больше, чем сможете проглотить: ни любви, ни жары, ни мятного ликера». В обоих случаях - свежие листья мяты придают натуральному спирту тонкий и обезоруживающий вкус свежести, и смелости превратить первый шаг в прыжок Кузнечика. 24%, 700 мл, Нидерланды. ', 'liqueur', 'ing_img/liqueur/mint_liquer.jpg', 'Ликеры и настойки', 1),
(111, 'Самбука светлая', '    Легенда пати-баров по всему миру, получившая признание за терпкий вкус и аромат аниса и сногсшибательный эффект. Травяной ликер производится по традиционному итальянскому рецепту. Для её приготовления используются лучшие анисовые цветки, выращенные в Китае, что придает ей богатый, многогранный вкус, который может привести к Невинному сексу или закончиться Пулей в голову Соколова. 38%, 700 мл, Италия.', 'liqueur', 'ing_img/liqueur/sambuca_lite.jpg', 'Ликеры и настойки', 1),
(112, 'Блю Курасао ликер', '    Возможно, самый узнаваемый ликер во всем мире, который станет главным героем любой коктейльной вечеринки. Классические кюрасао (голубой, красный, зеленый) абсолютно идентичны по вкусу и отличаются только своим цветом. Приятный аромат и вкус спелых апельсинов с острова Кюрасаю не оставляет равнодушных, и не важно - нежишься ли ты в Голубой лагуне или летаешь в Облаках. 24%, 700 мл, Нидерланды.', 'liqueur', 'ing_img/liqueur/blue_curasao_liquer.jpg', 'Ликеры и настойки', 1),
(113, 'Персиковый ликер', '    Уникальный персиковый ликер, открывший миру Секс на пляже, дает барменам массу возможностей для творчества. Вкус этого кристально чистого, удивительно легкого ликера как будто взрывается тонами свежих зрелых персиков. Крепость 20%.', 'liqueur', 'ing_img/liqueur/peach_liquer.jpg', 'Ликеры и настойки', 1),
(114, 'Черно-смородиновый ликер', '    Смородина из самого сердца Бургундии на полке твоего бара в любое время года. Экстракт спелых ягод смешивают с нейтральными спиртами, и вуаля - Зима уже не кажется такой холодной, и настроение становится Чики-пуки. Популярностью ликер обязан Феликсу Киру, придумавшему коктейль Кир Рояльx, который, впоследствии, прославил Крем де кассис на весь мир. 15%, 700 мл, Нидерланды.', 'liqueur', 'ing_img/liqueur/black_currant_liquer.jpg', 'Ликеры и настойки', 1),
(115, 'Апельсиновый ликер сухой', '    Дар голландских шкиперов средневековья нынешним ценителям тропической экзотики. Дистилляты из простого спирта в сочетании с цедрой апельсинов, лимонов и кюрасао смешиваются для получения более насыщенного цитрусового вкуса. И кто бы не оказался на самом деле первым — Дон Бичкомбер или Торговец Вик, Май Тай безусловно стал флагманом не только тики культуры, но и апельсинового сухого ликера. 30%, 700 мл, Нидерланды. ', 'liqueur', 'ing_img/liqueur/orange_dry_liquer.jpg', 'Ликеры и настойки', 1),
(116, 'Ликер какао светлый', '    Младший брат темного какао появился на свет в то время, когда секс стал доступен, а настоящая любовь спряталась. Традиционный шоколадный дистиллят на основе простого спирта и какао-бобов с ванилью проходит дальнейшую дистилляцию и очистку для придания мягкого вкуса белого шоколада. Благодаря Кузнечику с какао даже Радж из «Теории большого взрыва» с легкостью заводил разговор с любой красоткой. 24%, 700 мл, Нидерланды. ', 'liqueur', 'ing_img/liqueur/cacao_light_liquer.jpg', 'Ликеры и настойки', 1),
(117, 'Лимончелло', '    Самый любимый дижестив от Милана до Сардинии, без которого не может обойтись ни одна итальянская вечеринка и уважающий себя бар. Лимонный ликер готовят путем настаивания лимонной цедры в течение 3-5 дней. И неважно пьешь ли ты его с видом на Тверскую улицу или Коллизей, прекрасное настроение гарантировано. 26%, 700 мл, Италия.', 'liqueur', 'ing_img/liqueur/limoncello.jpg', 'Ликеры и настойки', 1),
(118, 'Вишневый ликер', '    Афродизиак для одиноких сердец, которым все время приходится кого-то соблазнять. Высушенные на солнце плоды темной вишни измельчают вместе с косточками и настаивают на бренди с добавлением сахара и, впоследствии, подвергают двойной дистилляции. Благодаря косточкам насыщенный вишневый бренди приобретает миндальные нотки во вкусе и становится страстным, как Рудольфо в одноименном коктейлю фильме — Кровь и песок. 24%, 700 мл, Нидерланды. ', 'liqueur', 'ing_img/liqueur/cherry_liquer.jpg', 'Ликеры и настойки', 1),
(119, 'Гальяно', '    Со временем великого генерала Джузепе Гальяно - герой не храбрее обычного человека, просто в его коктейле был Гальяно. В процессе производства ликер проходит 6 дистилляций и 7 мацераций с использованием порядка 30 различных ингредиентов — альпийские ягоды, ваниль, анис, мята и лаванда...Уносит страхи и предрассудки, как Ураган. 30%, 1000 мл, Италия.', 'liqueur', 'ing_img/liqueur/galliano.jpg', 'Ликеры и настойки', 1),
(120, 'Малибу', '    Ромово-кокосовый ликер, от которого душа поет, а сердце бьется радостнее. Если бы пираты пили Малибу, они бы однозначно были добрее. Ликер появился в 1980 году в Великобритании, и представляет собой смесь белого рома и кокоса. Тропический аромат создает ощущение отдыха на Карибском побережье, когда под рукой Кокосовый бриз в кувшине. 21%, 700 мл, Великобритания.', 'liqueur', 'ing_img/liqueur/malibu.jpg', 'Ликеры и настойки', 1),
(121, 'Ликер личи', '    Магическое очарование Азии в твоем баре. Ликер создан на основе эссенции экзотических китайских фруктов личи. Обладает специфическим вкусом, немного напоминающим виноград, который отлично дополнил вкусовую симфонию в Маске зла и Александре. 20%, 700 мл, Нидерланды. ', 'liqueur', 'ing_img/liqueur/lychee_liquer.jpg', 'Ликеры и настойки', 1),
(122, 'Яблочный ликер зеленый', '    Запретный плод в твоем баре, за наслаждение которым в свое время горько поплатились Адам и Ева. К счастью, сегодня кисло-сладким вкусом свежих яблок можно наслаждаться безнаказанно. Поэтому смело пьем Абсент без разрешения, не опасаясь последствий. 15%, 700 мл, Нидерланды. ', 'liqueur', 'ing_img/liqueur/green_apple_liqueur.jpg', 'Ликеры и настойки', 1),
(123, 'Банановый ликер желтый', '    Дух Африки и вкус детства в одной бутылке. Банановый ликер создается на основе экстрактов спелых бананов и нейтральных спиртов. Его сладость кажется избыточной, поэтому чаще всего банановый ликер используют в коктейлях, и Обезьяньи мозги - очень вкусное тому подтверждение. 24%, 700 мл, Нидерланды.', 'liqueur', 'ing_img/liqueur/banana_yellow_liqueur.jpg', 'Ликеры и настойки', 1),
(124, 'Драмбуи', '    Отец всех ликеров на виски, причем в его состав входит около 30 молтов из Шотландии. Долгое время рецепт приготовления, подаренный принцем Чарльзом Стюартом семье МакКинон, держался в секрете. Но сейчас доподлинно известно, что в состав ликеров входит вересковый мед, шафран, гвоздика, корица и лесные травы. 40%, 700 мл, Шотландия.', 'liqueur', 'ing_img/liqueur/drambuie.jpg', 'Ликеры и настойки', 1),
(125, 'Кокосовый ликер', '    Мечта из рекламы Баунти, ставшая явью в твоем баре. Соблазнительный ликер, сделанный на основе кокоса и белого рома буквально незаменим в приготовлении тропических коктейлей, например красотки в очередь выстраиваются за Пина коладой. 20%, 700 мл, Нидерланды.', 'liqueur', 'ing_img/liqueur/coconut_liqueur.jpg', 'Ликеры и настойки', 1),
(126, 'Ликер корицы', '    Мистическая королева специй в твоем баре, которая по легенде приносит удачу. Ликер обладает насыщенным ароматом корицы и цитрусовых. Когда гости начинают падать с ног после Водевиля, звони - 911. 50%, 500 мл, Нидерланды.', 'liqueur', 'ing_img/liqueur/koritsa_liqueur.jpg', 'Ликеры и настойки', 1),
(127, 'Ликер красный грейпфрут', '    Магнит для девушек в твоем баре. Есть мнение, что, если съедать один грейпфрут в день, то лишние килограммы пропадают сами собой. Достоверного доказательства этому факту нет, зато Барбадоска достоверно подтверждает, что нежный кисло-сладких вкус поднимает жизненный тонус. 15%, 700 мл, Нидерланды.', 'liqueur', 'ing_img/liqueur/red_grapefruit_liqueur.jpg', 'Ликеры и настойки', 1),
(128, 'Бенедиктин', '    Непоколебимый долгожитель из монастыря святого Бенедектина, разменявший совсем недавно добрую половину тысячелетия. Около 27 трав и специй проходят четыре разных этапа дистилляции и мацерации на основе коньячного и натурального спирта перед дальнейшей выдержкой в течении 17 месяцев до разлива в бутылки. И да! Не уйдет ни одна бутылка в продажу без печати настоятеля, а то так можно и подорвать национальное достояние Сингапура — коктейль Сингапурский слинг. 40%, 750 мл, Франция. ', 'liqueur', 'ing_img/liqueur/benedictine.jpg', 'Ликеры и настойки', 1),
(129, 'Ежевичный ликер', '    Ягодный бум и ежевичное настроение в любой день в твоем баре. Ликер готовится на основе экстрактов спелых ягод и бренди. Обладает мягким ежевичным ароматом и сочным насыщенным вкусом. Последнее время все больше и больше барменов по всему миру используют его, и Большой яблочный мартини - наилучшее тому подтверждение. 20%, 700 мл, Нидерланды.', 'liqueur', 'ing_img/liqueur/blackberry_liqueur.jpg', 'Ликеры и настойки', 1),
(130, 'Ревеневый ликер', '    С грядки в бар. Привычный русскому вкусу в компоте, ревень занял почетное место в линейке ликеров благодаря кисло-сладкому вкусу и приятному розовому цвету. Предаваться безудержному веселью вдвойне приятно в компании Его Величества - Королевского ревеня. 15%, 700 мл, Нидерланды.', 'liqueur', 'ing_img/liqueur/reven_liqueur.jpg', 'Ликеры и настойки', 1),
(131, 'Клубничный ликер', '    Царица сердец среди ягод, не требующая дополнительного представления, в твоем баре. Ликер ярко-красного цвета отличается удивительным ароматом зрелой лесной земляники и дарит свежесть летнего утра даже Краснокожему мачо. 23%, 700 мл, Нидерланды.', 'liqueur', 'ing_img/liqueur/strawberry_liqueur.jpg', 'Ликеры и настойки', 1),
(132, 'Ликер манго', '    После пары манго - мы танцуем танго. Ликер готовится из плодов, созревших под ласковым солнцем и отличается характерным ароматом. Его сладкий, мягкий вкус и тропические нотки придают Манго дайкири еще большей притягательности. 15%, 700 мл, Нидерланды. ', 'liqueur', 'ing_img/liqueur/mango_liqueur.jpg', 'Ликеры и настойки', 1),
(133, 'Ликер маракуйя', '    Источник неподдельной страсти в твоем баре. Тонизирующий фруктовый ликер, сделан на основе маракуйи, апельсинов и грейпфрута. Отличается не только ярким насыщенным цветом, но и горячим характером тропических фруктов. Не зря все Порнозвезды души в нем не чают и пьют перед съемками, чтобы отдаться во власть истинных чувств. 20%, 700 мл, Нидерланды. ', 'liqueur', 'ing_img/liqueur/passionfruit_liqueur.jpg', 'Ликеры и настойки', 1),
(134, 'Самбука яблочная', '    Яблочная самбука Antica производится по классическому рецепту с добавлением зеленых яблок, собранных вручную в области Южный Тироль, в регионе Трентино Альто Адидже, там, где Италия граничит с Австрией. Крепость 38%.', 'liqueur', 'ing_img/liqueur/sambuca_apple.jpg', 'Ликеры и настойки', 1),
(135, 'Яичный ликер', '    Единственный и неповторимый. Всемирно известный голландский ликер состоит из 4 ингредиентов: яичных желтков, бренди, ванили и сахара, и готовится в соответствии с традиционными голландскими методами. Самому Адвокату дьявола пришлось потрудиться, чтобы достать бутылочку своему боссу и не превратиться в Раздавленную лягушку. Крепость 14,8%, 700мл, Нидерланды.', 'liqueur', 'ing_img/liqueur/advokat(egg_liqueur).jpg', 'Ликеры и настойки', 1),
(136, 'Бехеровка', '    Неудавшийся эксперимент с рецептурами Яна Бехера в поиске лекарства от холеры подарил миру Бехеровку. Чешский травяной ликер до сих пор готовится по секретному рецепту, который и по сей день знают только 2 человека. При производстве бленд из около 20 специй в холщовых мешках вымачивается в алкоголе, после чего экстракт мешают с водой и сахаром, а потом выдерживают в дубовых бочках 2-3 месяца. Мягкий вкус родного ликера так полюбился Карелу Готту, что после концертов он потягивает Богемный холодный чай. 38%, 500 мл, Чехия.', 'liqueur', 'ing_img/liqueur/becherocka.jpg', 'Ликеры и настойки', 1),
(137, 'Ликер какао коричневый', '    Книга с легкостью становится окном, сквозь которое выглядывает душа, если у тебя есть ключ. Традиционный шоколадный ликер создан на основе простого спирта, выдержанного на экзотических какао-бобах из Африки и ванили из Мадагаскара. По одной из версий Бренди Александр, получил свое название от критика Александра Вуллкота , основателя «Круглого Стола Алгонкина» на 5-ой авеню, куда входила элита искусства театра, литературы и кино. 24%, 700 мл, Нидерланды. ', 'liqueur', 'ing_img/liqueur/cacao_brown_liqueur.jpg', 'Ликеры и настойки', 1),
(138, 'Самбука банановая', '    Самбука Antica со вкусом банана — производится по классическому рецепту, с добавлением зрелых бананов. В результате подобной комбинации получился великолепный натуральный вкус, который часто используется для приготовления коктейлей и десертов с банановым вкусом. Крепость 38%.', 'liqueur', 'ing_img/liqueur/sambuca_banana.jpg', 'Ликеры и настойки', 1),
(139, 'Самбука вишневая', '    Вишневая самбука Antica это прекрасная комбинация пикантного вкуса красной итальянской вишни и кристально чистой классической самбуки. Крепость 38%.', 'liqueur', 'ing_img/liqueur/sambuca_cherry.jpg', 'Ликеры и настойки', 1),
(140, 'Самбука апельсин-манго', 'Только идеально созревшие плоды апельсинов и манго, выращенных на юге Сицилии, используются для приготовления самбуки Antica с мощным и ярким вкусом апельсина и манго. Крепость 38%.', 'liqueur', 'ing_img/liqueur/sambuca_orange_mango.jpg', 'Ликеры и настойки', 1),
(141, 'Шартрез зеленый', '    Французский травяной ликер, окутанный тайной. Исторически готовился монахами картезианского ордена, жившими на границе горного массива Шартрез в XVII веке, которые передавали рецепт из поколения в поколение. 55%, 700 мл, Франция. ', 'liqueur', 'ing_img/liqueur/charteuese_green.jpg', 'Ликеры и настойки', 1),
(142, 'Южный комфорт', 'Залог домашнего уюта и душевной теплоты в твоем баре. Смесь бурбона, фруктов и огромного числа трав, но точный рецепт знают лишь 8 человек в мире. Известно, что в состав входят апельсины, персики, лимоны, корица и ваниль, а ликер выдерживают в дубовых бочках. Изысканный вкус не оставляет равнодушных, и ничего не остается, как быть Очарованным. 38%.', 'liqueur', 'ing_img/liqueur/southe_comfort.jpg', 'Ликеры и настойки', 1),
(143, 'Яблочный ликер желтый', '    Аромат осени и вкус бабушкиного варенья в твоем баре. Ликер изготавливается путем дистилляции яблочного сока. В аромате доминирует интенсивный яблочный запах, а сладкий и фруктовый вкус идеально подходит для Яблочного пирога, от которого без ума сама Гейша мартини. 18%, 700 мл.', 'liqueur', 'ing_img/liqueur/apple_yellow_liqueur.jpg', 'Ликеры и настойки', 1),
(144, 'Дженевер', '    Традиционный голландский напиток с 400-летней историей. Гордость нации, без которого не обходятся ни одни посиделки дружной компании, и не может обойтись твой бар. Дженевер - это смесь тройного солодового дистиллята с ягодами можжевельника и травами, добавленными во время четвертой дистилляции. Обладает легким приятным ароматом, в котором отлично сочетаются солод и можжевельник. Голландский базил смэш - лучшее тому подтверждение. 35%, 1000 мл, Нидерланды.', 'liqueur', 'ing_img/liqueur/genever_liqueur.jpg', 'Ликеры и настойки', 1),
(145, 'Ликер голубики', '    Голубая кровь в твоем баре. Наслаждаться вкусом свежей голубики круглый год могут теперь не только члены королевских семей и аристократия, но и ценители коктейлей. Сладкий бесцветный ликер бьет точно в цель, также как и Ворошиловский стрелок. 15%, 700 мл, ', 'liqueur', 'ing_img/liqueur/blueberry_liqueur.jpg', 'Ликеры и настойки', 1),
(146, 'Малиновый ликер', '    Яркий акцент в твоем баре, на зов которого собираются красотки, сладкоежки и мишки. Ликер ярко-красного цвета со вкусом свежих сочных ягод бодрит в любую погоду, и пока не один медведь не смог устоять перед его вкусом, например, в коктейле Блинк-блинк. 16%, 700 мл, Нидерланды. ', 'liqueur', 'ing_img/liqueur/raspberry_liqueur.jpg', 'Ликеры и настойки', 1),
(147, 'Самбука малиновая', '    Самбука Antica с малиновым вкусом — одна из наиболее популярных фруктовых самбук. Она производится по классическому рецепту с добавлением зрелой малины, выращенной в Италии. Крепость 38%.', 'liqueur', 'ing_img/liqueur/sambuca_raspberry.jpg', 'Ликеры и настойки', 1),
(148, 'Фиалковый ликер', '    Полет фантазии в твоем баре. Ликер получается, когда коньячный или натуральный спирт ароматизируется методом мацерации цветков фиалки из горных Альп и окрашивается для романтического эффекта. Братья Райт мечтали и смогли преодолеть силу притяжения Земли, дав толчок развитию Авиации во всем мире. Одноименный коктейль стал для многих символом надежды, и как сказал, разбившись на планере Лилиенталь, — «Жертвы не напрасны». 22%, 500 мл, Германия.', 'liqueur', 'ing_img/liqueur/violet_liqueur.jpg', 'Ликеры и настойки', 1),
(149, 'Мандариновый ликер', 'Великий император Наполеон не мог и дня прожить без своего любимого напитка, и твой бар тоже не cможет. Ликер с более чем двухсотлетней историей изготавливается путем дистилляции цедры мандаринов, собранных на лучших плантациях Корсики и Сицилии. В состав входят травы, специи и лучшие коньячные спирты выдержки не менее 6 лет, обогащающие вкус и аромат напитка. Прикоснуться к к великой истории так просто с коктейлем Дух''57. 38%, 700 мл, Бельгия.', 'liqueur', 'ing_img/liqueur/mandarine_liqueur.jpg', 'Ликеры и настойки', 1),
(150, 'Амаро', '    Страстный итальянец, покоритель сердец и полиглот в твоем баре. Травяной ликер с горько-сладким вкусом производится путём настаивания трав, корней, цветов, коры и цедры на крепком алкоголе или вине. Если Открытка из Италии вдруг затерялась на почте, Пиммс №6 поможет сгладить горечь разочарования. 16-35%, Италия. ', 'liqueur', 'ing_img/liqueur/amaro.jpg', 'Ликеры и настойки', 1),
(151, 'Арбузный ликер', '    Зелененький, полосатенький, в серединке сладенький. Летнее настроение в твоем баре. Ликер красного цвета готовят из зрелых, насыщенных соком арбузов. Сладкий фруктовый вкус станет отличным дополнением к просмотру культового фильма - Лицо со шрамом Брайнана да Пальмы. 20%, 700 мл, Нидерланды.', 'liqueur', 'ing_img/liqueur/watermelon_liqueur.jpg', 'Ликеры и настойки', 1),
(152, 'Банановый ликер зеленый', 'Дух Африки и вкус детства в твоем баре. Банановый ликер создается на основе экстрактов спелых бананов и нейтральных спиртов. Чаще всего ликер Pisang используют в качестве ингредиента для экзотических коктейлей, однако никто не ограничивает свободу экспериментов, и Бубль гум - отличное этому подтверждение. 24%, 700 мл, Нидерланды.', 'liqueur', 'ing_img/liqueur/banana_green_liqueur.jpg', 'Ликеры и настойки', 1),
(153, 'Ирисковый ликер', '    Конфеты для взрослых в твоем баре. Ликер уже многие годы остается хитом «коктейльных чартов» в США. Во вкусе ликера переплетаются оттенки ириса, карамели и свежих сливок. Его можно пить с молоком в качестве лонг-дринка, добавлять в мороженое и в кофе, а можно например приготовить озорного Петрушку. 15%, 700 мл, Нидерланды.', 'liqueur', 'ing_img/liqueur/toffee_liqueur.jpg', 'Ликеры и настойки', 1),
(154, 'Ликер из цветков бузины', '    Таинственный незнакомец за твоим баром. Сладкий ликёр, изготовленный методом мацерации из цветков бузины с добавлением персика, апельсина, грейпфрута, груши и сахарного сиропа в конце. Его вкус часто сравнивают со вкусом личи, но Генриетта Хадсон не готова променять его не на что на свете. 22%, 500 мл, Германия.', 'liqueur', 'ing_img/liqueur/buzina_liqueur.jpg', 'Ликеры и настойки', 1),
(155, 'Ликер фалернум', '    Тропический ветер, вкус солнца и оранжевого настроения в твоем баре. Ликер с историей и вкусовыми нотками миндаля, имбиря, лайма и гвоздики. Часто используется в тропических коктейлях и в коктейлях на основе рома, и трудно представить вкус Пунша плантатора без ликера Фалернум. 11%, 700 мл.', 'liqueur', 'ing_img/liqueur/falernum.jpg', 'Ликеры и настойки', 1),
(156, 'Мятный ликер светлый', '«Удаляет жало» и «вынимает топор из головы». Ничем не отличается от мятного зеленого ликера, кроме цвета, достигаемого за счет разных степеней очистки. В начале 20 века в книге Билла Бутсби появилась первая рецептура идеального послеобеденного и даже утреннего дижестива Стингер от Джей Си О''Коннора, хозяина красивейшего в мире джентльменского кафе. 24%, 700 мл, Нидерланды. ', 'liqueur', 'ing_img/liqueur/mint_lite_liqueur.jpg', 'Ликеры и настойки', 1);
INSERT INTO `ingridients` (`id`, `ing_title`, `ing_desc`, `ing_type`, `ing_img`, `ing_type_rus`, `is_ing`) VALUES
(157, 'Сливовый джин', '    Старый новый знакомый в твоем баре. Ликер из ягод терновника настаивают на джине, благодаря чему напиток приобретает темный цвет и характерный аромат. Не стесняйся экспериментов или положись на проверенные временем сочетания, как, например, в шоте Маленький хаммер. 20%, 700 мл, Нидерланды. ', 'liqueur', 'ing_img/liqueur/plum_liqueur.jpg', 'Ликеры и настойки', 1),
(158, 'Ликер пименто драм', '    Секретный ингредиент в твоем баре. Ликер пименто драм готовится на основе ямайского рома с добавлением сушеных ягод перечного дерева. Пряный вкус и аромат соединяет в себе нотки гвоздики, ванили и корицы. 22%, 750 мл, Германия.', 'liqueur', 'ing_img/liqueur/pimento_dram_liqueur.jpg', 'Ликеры и настойки', 1),
(159, 'Имбирный ликер', '    Конфеты для взрослых в твоем баре. Ликер уже многие годы остается хитом «коктейльных чартов» в США. Во вкусе ликера переплетаются оттенки ириса, карамели и свежих сливок. Его можно пить с молоком в качестве лонг-дринка, добавлять в мороженое и в кофе, а можно например приготовить озорного Петрушку. 15%, 700 мл, Нидерланды.', 'liqueur', 'ing_img/liqueur/imbir_liqueur.jpg', 'Ликеры и настойки', 1),
(160, 'Киллепич', '    Легендарная настойка родом из самого сердца Германии - Дюссельдорфа. Напиток кроваво-красного цвета в состав которого входят 90 разных фруктов, ягод, трав и специй, поможет согреться и сохранить присутствие духа даже в самые суровые холода. 42%, 700 мл, Германия.', 'liqueur', 'ing_img/liqueur/killerpitsch.jpg', 'Ликеры и настойки', 1),
(161, 'Ликер лимонника', '    Магнит для любителей экзотики в твоем баре. Соблазнительный ликер с ярким вкусом лимонника и бесподобным ароматом дарит изумленные улыбки и только приятные воспоминания. 26%, 700 мл, Нидерланды.', 'liqueur', 'ing_img/liqueur/lemongrass_liqueur.jpg', 'Ликеры и настойки', 1),
(162, 'Игристое вино сухое', 'Каждый заслуживает игристого в победе также сильно, как и нуждается в нем в поражении. Собранный еще до созревания виноград сначала осторожно выжимают и выдерживают в стальных бочках, затем смешанное вино разливают в бутылки с добавлением дрожжей и сахара (не более 12 гр на литр), спустя 12 месяцев бутылки подвергаются ремюажу и дегоржированию. 13%, 750 мл, Франция.', 'sparking_wine', 'ing_img/sparking_wine/sparking_dry_wine.jpg', 'Игристые вина', 1),
(165, 'Вишневый сок', '    Вишневый сок Rich делается из лучших сортов спелых ягод. Наслаждайся терпким вкусом и нежным, бархатным послевкусием в Вишневом сауэре или Вишне и вишне.', 'juice', 'ing_img/juice/cherry_juice.jpg', 'Соки и морсы', 1),
(166, 'Томатный сок', '    В 1921 году бармен Фердинанд Пит Петио из «Гарриз Бар» в Париже впервые смешал водку с американской новинкой - пакетированным томатным соком, - получив первую Кровавую Мэри. Кстати, самую вкусную «Мэри» подают в самолетах, ведь на высоте все соки, кроме томатного, сильно теряют во вкусе. Из 2 кг помидоров получается до 1,5 л сока, однако барные карты, включающие свежевыжатый томатный сок, пока не обнаружены. Лучшие томаты из дарят невероятно насыщенный и мягкий вкус густому соку Rich. 1 л', 'juice', 'ing_img/juice/tomato_juice.jpg', 'Соки и морсы', 1),
(167, 'Гранатовый сок', 'Содержит значительное количество глюкозы и фруктозы, органические кислоты и 15 аминокислот, в том числе 6 незаменимых, витамины: С, В1, В2, Е, А, РР и другие микроэлементы. Рекомендуется при лечении гипертонии и болезней желудочно-кишечного тракта. Способствует повышению уровня гемоглобина в крови.', 'juice', 'ing_img/juice/granat_juice.jpg', 'Соки и морсы', 1),
(168, 'Персиковый сок', '    Персиковый сок Rich удивит ценителей вкусных фруктов необычайно нежным и мягким вкусом свежих фруктов, отобранных в разных странах. Пеликан или Двойное удовольствие с персиковым соком Rich подарят океан ярких ощущений.', 'juice', 'ing_img/juice/peach_juice.jpg', 'Соки и морсы', 1),
(169, 'Березовый сок', '    Напиток самой природы. В составе березового сока биологически активные вещества и соединения, минералы: калий, кальций, натрий, магний.', 'juice', 'ing_img/juice/bereza_juice.jpg', 'Соки и морсы', 1),
(170, 'Виноградный сок красный', '    Только лучшие сорта винограда с плантаций Испании отбираются, чтобы стать соком Rich. Необычный вкус, насыщенный букетом бархатных нот и чуть терпким послевкусием, станет отличным дополнением для Виноградного слига и подарит солнечное настроение даже в самый дождливый день. ', 'juice', 'ing_img/juice/grape_juice.jpg', 'Соки и морсы', 1),
(171, 'Манговый сок', '    Манговый сок Rich делается из самых сочных плодов, собранных в Мексике и Индии. Необычное сочетание дарит яркий кисло-сладкий вкус с хвойными нотками. Наслаждайся вкусом спелых фруктов, который дарит настоящее тропическое солнце вместе с Лав ми ду.', 'juice', 'ing_img/juice/mango_juice.jpg', 'Соки и морсы', 1),
(172, 'Брусничный морс', '    Хотя целебными свойставми обладают в основном листья брусники, морс из ягод улучшает аппетит, помогает при повышенном кровяном давлении, кашле и простуде. ', 'juice', 'ing_img/juice/brusnika_juice.jpg', 'Соки и морсы', 1),
(173, 'Облепиховый сок', '    Содержит почти все существующие витамины: А, F, E, K, P, C, витамины группы В, флафоноиды, сахар, жирные и органические кислоты. Стимулирует пищеварение, повышает иммунитет и уровень гемоглобина. ', 'juice', 'ing_img/juice/oblepiha_juice.jpg', 'Соки и морсы', 1),
(174, 'Ягодный морс', '    Напиток из сока ягод, разбавленных водой и сахаром. Объединяет в себе вкус и пользу сразу нескольких составляющих. ', 'juice', 'ing_img/juice/berry_juice.jpg', 'Соки и морсы', 1),
(175, 'Абрикосовый сок', '    Содержит много солей кальция и железа, поэтому он очень полезен маленьким детям и беременным женщинам. 150 г сока удовлетворяют суточную потребность человека в каротине. Сок усваивается организмом легче, чем плоды.', 'juice', 'ing_img/juice/apricot_juice.jpg', 'Соки и морсы', 1),
(176, 'Арбузный сок', '    Прекрасно утоляет жажду. Полезен при заболеваниях почек и обладает способностью выводить из организма холестерин. ', 'juice', 'ing_img/juice/watermelon_juice.jpg', 'Соки и морсы', 1),
(177, 'Банановый сок', '    Содержит витамин С, В2, В6, фолиевую кислоту. Снижает содержание холестерина в крови, повышает иммунитет и улучшает качество сна. ', 'juice', 'ing_img/juice/banana_juice.jpg', 'Соки и морсы', 1),
(178, 'Мандариновый сок', 'Мандариновый сок немного слаще и мягче, чем другие цитруовые соки. Используется как жаропонижающее и противовоспалительное средство, помогает при гипертонии и авитаминозе. Полезен при заболеваниях желудочно-кишечного тракта. Благодаря фитонцидам, содержащимся в нем, обладает противомикробным действием, повышает тонус и укрепляет иммунитет.', 'juice', 'ing_img/juice/mandarine_juice.jpg', 'Соки и морсы', 1),
(179, 'Мульти-фруктовый сок', '    Мультифруктовый сок Rich – великолепный и легкий микс экзотических фруктов со всего света. Сочная мякоть дарит неповторимое удовольствие, а невероятно богатый и яркий вкус этого сока наполнит фруктовым наслаждением Мульти смэш.', 'juice', 'ing_img/juice/multi_fruit_juice.jpg', 'Соки и морсы', 1),
(180, 'Апельсиновый сок красный', '    Коктейль из красного апельсинового сока и шотландского виски, стал символом Голливуда еще в 1930 году. Из одного апельсина можно выжать немногим больше 100 мл сока. Апельсиновый сок Rich делается из лучших сортов красных апельсинов, созревших под ярким тропическим солнцем. 1 л', 'juice', 'ing_img/juice/orange_red_juice.jpg', 'Соки и морсы', 1),
(181, 'Виноградный сок светлый', '    Является наиболее питательным и калорийным соком. Виноградный сок занимает одно из первых мест по содержанию витамина Р, который благотворно действует на укрепление капиллярных сосудов. Виноградный сок повышает обмен веществ в организме, благотворно действует при легочных заболеваниях, а также заболеваниях сердечно-сосудистой системы, болезнях почек и печени, способствует быстрому восстановлению сил. ', 'juice', 'ing_img/juice/grape_light_juice.jpg', 'Соки и морсы', 1),
(182, 'Сок алоэ', '    Сок алоэ обладает невероятными заживляющими свойствами и оказывает общее успокаивающее действие. Повышает аппетит и улучшает пищеварение.', 'juice', 'ing_img/juice/aloe_juice.jpg', 'Соки и морсы', 1),
(183, 'Тоник', '    Газированный напиток с хинином, обладающий горько-кислым вкусом. Был изобретен в период британского правления в колониальной Индии.', 'water_drink', 'ing_img/water_drink/tonick.jpg', 'Вода и напитки', 1),
(184, 'Биттер лемон', 'Газированный напиток, появившийся в начале 60-х. Биттер лемон содержит натуральный лимонный сок, который придает ему золотистый оттенок. ', 'water_drink', 'ing_img/water_drink/bitter-lemon.jpg', 'Вода и напитки', 1),
(185, 'Энергетик', '    Энергетический напиток был специально разработан для употребления в периоды повышенной физической и умственной нагрузки.', 'water_drink', 'ing_img/water_drink/energetic.jpg', 'Вода и напитки', 1),
(186, 'Квас', '    Древнерусский газированный слабоалкогольный напиток, известный более 1000 лет. Получается в результате незавершенного спиртового или спиртового и молочного брожения сусла. Содержание спирта не превышает 1,2%. Квас полезен для пищеварения, улучшает обмен веществ и благотворно влияет на сердечно-сосудистую систему. <1,2%, 500 мл, Россия.', 'water_drink', 'ing_img/water_drink/kvas.jpg', 'Вода и напитки', 1),
(187, 'Холодный зеленый чай', '    В Китае зеленый чай считают лекарством, в Японии считается, что он продлевает жизнь и оздоравливает сердце. Зеленый чай тонизирует, бодрит и придает силы. Несколько чашек зеленого чая в день обеспечивает профилактику сердечно-сосудистых заболеваний. Антиоксиданты, содержащиеся в зеленом чае, поднимают иммунитет и замедляют старение кожи.', 'water_drink', 'ing_img/water_drink/ice_green_tea.jpg', 'Вода и напитки', 1),
(188, 'Холодный персиковый чай', '    Прохладительный напиток на основе чая и фруктового сока. Обладает приятным вкусом с легкой кислинкой.', 'water_drink', 'ing_img/water_drink/ice_peach_tea.jpg', 'Вода и напитки', 1),
(189, 'Лимонад дюшес', '    Освежающий лимонад с нежным грушевым ароматом. Свое имя получил от сорта десертных груш с нежной и сладкой мякотью, названного в честь французской герцогини Ангулемской (от фр. duchesse, буквально – герцогиня). Cо времен первых рецептов лимонада в XVII веке его состав претерпел значительные изменения; тогда он состоял только из лимонного сока и воды. С началом массового производства в напиток стали добавлять сиропы-ароматизаторы, подсластители и двуокись углерода. Рекомендуется употреблять в охлажденном виде. Хранят при температуре от 0 до +18 °С не больше 6 месяцев. ', 'water_drink', 'ing_img/water_drink/limonade_pear.jpg', 'Вода и напитки', 1),
(190, 'Малиновый сироп', '    В страсти к малине был уличен еще Марк Твен, предпочитавший знаменитый коктейль Кловер Клаб – один из главных хитов американских баров до введения сухого закона. Сироп производится из перебродившего сока спелых ягод, воды и сахара. Путем брожения из ягод убирают пектин, чтобы в процессе варки сироп не загустел. Открытую бутылку уверенно можно хранить 3 месяца. Производимые во Франции с 1912 года, сиропы Monin были признаны бартендерами из 32 стран мира. 1000 мл, Франция.', 'syrup', 'ing_img/syrup/raspberry__syrup.jpg', 'Сиропы', 1),
(191, 'Миндальный сироп', '    Даже самый дорогой в мире Май Тай стоимостью в 750 фунтов стерлингов в шикарном баре отеля «Merchant», не обходится без миндального сиропа. Вопреки расхожему мнению, миндаль часто причисляют к орехам, основной ингредиент сиропа является плодом рода сливовых. К счастью, именно благодаря изобретению сиропа миндаль из Азии перебрался в штаты и гостья Трейдера Вика Кэрри, произнесла по-таитянски «Роа Аэ», что означало «Внеземной — Самый Лучший». 1000 мл, Франция.', 'syrup', 'ing_img/syrup/mindal_syrup.jpg', 'Сиропы', 1),
(192, 'Сироп корицы', '    Monin производит отличные сиропы во Франции с 1912 г. Богат изобилием ароматов, фруктов и цветовых оттенков. Бармены 32 стран признали Monin лучшей маркой сиропов.', 'syrup', 'ing_img/syrup/koritsa_syrup.jpg', 'Сиропы', 1),
(193, 'Карамельный сироп', '    Monin производит отличные сиропы во Франции с 1912 г. Богат изобилием ароматов, фруктов и цветовых оттенков. Бармены 32 стран признали Monin лучшей маркой сиропов.', 'syrup', 'ing_img/syrup/caramel_syrup.jpg', 'Сиропы', 1),
(194, 'Кленовый сироп', '    Monin производит отличные сиропы во Франции с 1912 г. Богат изобилием ароматов, фруктов и цветовых оттенков. Бармены 32 стран признали Monin лучшей маркой сиропов.', 'syrup', 'ing_img/syrup/klen_syrup.jpg', 'Сиропы', 1),
(195, 'Сироп агавы', '    Сок агавы (многолетнего мексиканского растения, разновидности алоэ). Часто используется как заменитель сахара при приготовлении коктейлей, кондитерских изделий и мороженого. По вкусу напоминает мед.', 'syrup', 'ing_img/syrup/agava_syrup.jpg', 'Сиропы', 1),
